import React, { useState, useEffect } from 'react';
import Modal from 'react-modal';
import styled from 'styled-components';
import { obtenerDatos } from '../helper/traeDatos';
import { ProductoDetalle } from './ProductoDetalle';

const ModalContent = styled.div`
  background: #333;
  padding: 20px;
  border-radius: 8px;
  color: #fff;
  max-width: 500px;
  width: 100%;
  height: auto;
  margin: 0 auto;
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
  gap: 15px;
  align-items: center;
`;

const Label = styled.label`
  width: 100%;
  color: #bbb;
  font-size: 14px;
`;

const Input = styled.input`
  width: 100%;
  padding: 10px;
  background-color: #555;
  color: #fff;
  border: none;
  border-radius: 4px;
  font-size: 16px;
  outline: none;

  &::placeholder {
    color: #ccc;
  }
`;

const Button = styled.button`
  background-color: #e0a800;
  color: #000;
  border: none;
  padding: 10px 20px;
  font-size: 16px;
  border-radius: 4px;
  cursor: pointer;
  margin-top: 10px;

  &:hover {
    background-color: #ffd700;
  }
`;

const ModalOrdenes = ({ isModalOpen, toggleModal }) => {
  const [productos, setProductos] = useState([]);
  const [detalles, setDetalles] = useState([]);

  useEffect(() => {
    const cargarProductos = async () => {
      try {
        const url = "http://localhost:8080/public/productos";
        const data = await obtenerDatos(url, 'GET');
        setProductos(data);
      } catch (error) {
        console.error('Error al cargar los productos:', error);
      }
    };

    cargarProductos();
  }, []);

  // Función para agregar un nuevo ProductoDetalle
  const handleAgregarDetalle = () => {
    if (productos.length > 0) {
      setDetalles([...detalles, { productos }]);
    } else {
      console.warn('Productos no disponibles para agregar.');
    }
  };

  // Función para quitar el último ProductoDetalle
  const handleQuitarDetalle = () => {
    setDetalles(detalles.slice(0, -1));
  };

  const enviarDatos = () => {
    return null;
  };

  return (
    <Modal isOpen={isModalOpen} onRequestClose={toggleModal} contentLabel="Formulario de Nueva Orden">
      <ModalContent>
        <h2 style={{ textAlign: 'center' }}>Nueva Orden</h2>
        <Form>
          <Button type="button" onClick={handleAgregarDetalle}>
            Agregar línea de producto
          </Button>
          <Button type="button" onClick={handleQuitarDetalle} disabled={detalles.length === 0}>
            Quitar Último Detalle del Producto
          </Button>

          {detalles.map((detalle, index) => (
            <ProductoDetalle key={index} productos={detalle.productos} />
          ))}

          <Label>Fecha de Carga:</Label>
          <Input type="datetime-local" />

          <Label>Fecha de Entrega:</Label>
          <Input type="datetime-local" />

          <Label>Subtotal:</Label>
          <Input type="number" placeholder="5000.0" />

          <Label>Total:</Label>
          <Input type="number" placeholder="5500.0" />

          <Label>Pagado:</Label>
          <Input type="number" placeholder="" />

          <Button type="submit" onClick={enviarDatos}>Guardar</Button>
          <Button type="button" onClick={toggleModal}>Cancelar</Button>
        </Form>
      </ModalContent>
    </Modal>
  );
};

export default ModalOrdenes;
