import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { obtenerDatos } from '../helper/traeDatos';
import { mostrarUnaOrden } from '../helper/url';

const OrderDetail = () => {
    const { id } = useParams();
    const [orden, setOrden] = useState(null);

    useEffect(() => {
        async function fetchOrden() {
            const datos = await obtenerDatos(mostrarUnaOrden(id), 'GET');
            setOrden(datos);
        }
        fetchOrden();
    }, [id]);

    if (!orden) return <div>Cargando detalle...</div>;

    return (
        <div>
            <h2>Detalle de la Orden #{orden.idOrden}</h2>
            <p><strong>Cliente:</strong> {orden.cliente}</p>
            <p><strong>Total:</strong> ${orden.total}</p>
            <p><strong>Pagado:</strong> {orden.pagado ? 'Sí' : 'No'}</p>
            {/* Podés agregar más info si tu orden tiene más campos */}
        </div>
    );
};

export default OrderDetail;
