import { useEffect, useState } from 'react';
import { obtenerDatos } from '../helper/traeDatos';
import { insumoFindById, manufacturadoFiindById } from '../helper/url';
import { useParams } from 'react-router-dom';
import styled from 'styled-components';
import { Label } from './ModalStyles';

export const ButtonHiellow = styled.button`
  background-color: #e0a800;
  color: #000;
  border: none;
  padding: 10px 20px;
  font-size: 16px;
  border-radius: 4px;
  cursor: pointer;
  margin-top: 10px;

  &:hover {
    background-color: #ffd700;
  }
`;

const Container = styled.div`
  max-width: 800px;
  margin: auto;
  padding: 20px;
  background-color: #222;
  color: #fff;
  border-radius: 10px;

  @media (max-width: 600px) {
    padding: 15px;
  }
`;

const Title = styled.h1`
  font-size: 1.8rem;
  margin-bottom: 20px;
`;

const FieldGroup = styled.div`
  display: flex;
  flex-direction: column;
  margin-bottom: 15px;

  label {
    margin-bottom: 5px;
    font-weight: bold;
  }

  input {
    padding: 10px;
    border-radius: 6px;
    border: none;
    background-color: #333;
    color: #fff;
    font-size: 1rem;

    &:focus {
      outline: 2px solid #555;
    }
  }
`;

const ProductoDetalleInfo = ({ token, manufacturado = false }) => {
    const { id } = useParams();
    const [producto, setProducto] = useState(null);
    const [denominacion, setDenom] = useState('');
    const [precio, setPrecio] = useState(0);
    const [botonEstado, setBotonEstado] = useState(false);

    useEffect(() => {
        async function cargar() {
            if (id != null && token) {
                const url = (!manufacturado) ? insumoFindById(id) : manufacturadoFiindById(id);
                const data = await obtenerDatos(url, 'GET', token);
                setProducto(data);
            }
        }
        cargar();
    }, [id, token]);

    useEffect(() => {
        if (denominacion != '' || precio != 0) {
            setBotonEstado(true);
        } else {
            setBotonEstado(false);
        }
    }, [denominacion, precio]);

    const handleChange = (field, value) => {
        setProducto(prev => ({
            ...prev,
            [field]: value
        }));
    };

    if (!producto || producto.eliminado == true) {
        return <h2>Cargando...</h2>;
    }

    return (
        <Container>
            <Title>Detalle de producto #{(!manufacturado) ? producto.idInsumo : producto.idProductoManufacturado}</Title>

            <FieldGroup>
                <label>Denominación</label>
                <input
                    type="text"
                    value={producto.denominacion || ''}
                    onChange={(e) => handleChange('denominacion', e.target.value)}
                />
            </FieldGroup>

            <FieldGroup>
                <label>Precio</label>
                <input
                    type="number"
                    value={producto.precio || ''}
                    onChange={(e) => handleChange('precio', e.target.value)}
                />
            </FieldGroup>

            {!manufacturado ? (
                <>
                    <FieldGroup>
                        <label>Es para elaborar</label>
                        <input
                            type="checkbox"
                            checked={producto.esParaElaborar || false}
                            onChange={(e) => handleChange('esParaElaborar', e.target.checked)}
                            disabled={true}
                        />
                    </FieldGroup>

                    <FieldGroup>
                        <label>Marca</label>
                        {producto.marca?.nombre ? (
                            <h3>{producto.marca.nombre}</h3>
                        ) : (
                            <h3>Sin marca</h3>
                        )}
                    </FieldGroup>
                </>
            ) : (
                <>
                    {producto.tiempo_estimado != null && (
                        <FieldGroup>
                            <Label>Tiempo estimado:</Label>
                            <h3>{producto.tiempo_estimado}</h3>
                        </FieldGroup>
                    )}

                    {producto.descripcion && (
                        <>
                            <label>Descripción:</label>
                            <h3><strong>{producto.descripcion}</strong></h3>
                        </>
                    )}
                    {Array.isArray(producto.insumos) && producto.insumos.length > 0 && (
                        <div className="min-h-0 w-full p-4 sm:p-6 bg-[#0b0f13] text-white rounded-2xl shadow-lg">
                            <header className="flex items-center justify-between mb-4">
                                <h2 className="text-lg sm:text-xl font-semibold tracking-wide">
                                    Ingredientes:
                                </h2>
                            </header>

                            <div className="hidden md:block overflow-hidden rounded-lg border border-[#17313a]/40">
                                <table className="w-full table-fixed text-left">
                                    <thead className="bg-[#071017] border-b border-[#17313a]">
                                        <tr>
                                            <th className="px-4 py-3 text-sm font-medium uppercase tracking-wider text-cyan-300">
                                                Nombre
                                            </th>
                                            <th className="px-4 py-3 text-sm font-medium uppercase tracking-wider text-cyan-300">
                                                Marca
                                            </th>
                                        </tr>
                                    </thead>
                                    <tbody className="bg-[#071217]">
                                        {producto.insumos.map((it, idx) => (
                                            <tr
                                                key={it.id ?? idx}
                                                className="border-b last:border-b-0 border-[#17313a] hover:bg-[#092127]"
                                            >
                                                <td className="px-4 py-3 text-sm font-medium">{it.denominacion}</td>
                                                <td className="px-4 py-3 text-sm opacity-95">{it.marca?.nombre}</td>
                                            </tr>
                                        ))}
                                    </tbody>
                                </table>
                            </div>


                            <div className="md:hidden grid gap-3">
                                {producto.insumos.map((it, idx) => (
                                    <article
                                        key={it.idInsumo ?? idx}
                                        className="p-3 rounded-xl border border-[#17313a] bg-gradient-to-b from-[#061019] to-[#071217] shadow-sm"
                                    >
                                        <div className="flex items-center justify-between">
                                            <h3 className="text-sm font-semibold text-white">{it.denominacion}</h3>
                                        </div>
                                        <p className="mt-2 text-xs opacity-90">
                                            Marca: <span className="font-medium">{it.marca?.nombre}</span>
                                        </p>
                                        <p className="mt-2 text-xs opacity-90">
                                            Stock actual: <span className="font-medium">{it.detalle?.stockActual}</span>
                                        </p>
                                        <p className="mt-2 text-xs opacity-90">
                                            Unidad medida: <span className="font-medium">{it.unidadMedida?.denominacion}</span>
                                        </p>
                                    </article>
                                ))}
                            </div>
                        </div>
                    )}
                </>
            )}

            <FieldGroup>
                <label>Detalle (stock actual / mínimo)</label>
                <input
                    type="text"
                    value={
                        producto.detalle
                            ? `${producto.detalle.stockActual} / ${producto.detalle.stockMinimo}`
                            : ''
                    }
                    readOnly
                />
            </FieldGroup>
            <ButtonHiellow disabled={botonEstado} />
        </Container>
    );
}
export default ProductoDetalleInfo;
