import React, { useState } from "react";
import styled from 'styled-components';

const Select = styled.select`
  width: 87%;
  padding: 10px;
  margin-bottom: 10px;
  margin-right: 10px;
  margin-left: 10px;
  background-color: #555; /* Fondo gris para los campos */
  color: #fff;
  border: none;
  border-radius: 4px;
  font-size: 16px;
  outline: none;

  &::placeholder {
    color: #ccc;
  }
`;
const CajaProducto = styled.div`
  display: inline-block;
  float: left;
  padding: 10px;
  border: #333;
  border-radius: 3px;
`;

const Number = styled.input`
  width: 80%;
  padding: 10px;
  background-color: #555; /* Fondo gris para los campos */
  color: #fff; /* Letras blancas */
  border: none;
  border-radius: 4px;
  font-size: 16px;
  outline: none;

  &::placeholder {
    color: #ccc;
  }
    `;
const Label = styled.label`
    width: 100%;
    color: #bbb;
    font-size: 14px;
  `;
export const ProductoDetalle = ({ productos }) => {
  const [selectedItem, setSelectedItem] = useState(null);
  const handleSelectChange = (e) => {
    const selectedItemid = parseInt(e.target.value, 10);
    const item = productos.find((item) => item.idProducto === selectedItemid);
    setSelectedItem(item);

  };
  return (
    <CajaProducto>
      <Label>Detalle</Label>
      <Select onChange={handleSelectChange}>
        <option value="">Seleccionar uno...</option>
        {productos.map((item) => (
          <option key={item.idProductoManufacturado} value={item.idProductoManufacturado}>
            {item.denominacion}
          </option>
        ))}
      </Select>
      <Label>Cantidad:</Label>
      <Number type='number' className='cant' />
      <hr />
    </CajaProducto>
  )
};
