import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import { ThemeProvider } from 'styled-components';
import Navbar from './components/Navbar';
import OrdersTable from './components/OrdersTable';
import Products from './components/Products';
import { darkTheme } from './components/theme';
import GlobalStyle from './globalStyles';
import styled from 'styled-components';
import { useAuth0 } from '@auth0/auth0-react';
import Modal from 'react-modal';

//difinir modal raiz
Modal.setAppElement('#root');

const HeaderContainer = styled.header`
  background-color: ${({ theme }) => theme.background};
  position: fixed;
  top: 0;
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.1);
  z-index: 1000;
  padding: 20px 0;
`;

const Logo = styled.img`
  height: 120px;
  margin-bottom: 10px;
`;

const Title = styled.h1`
  font-size: 36px;
  color: ${({ theme }) => theme.primaryButton}; /* Texto amarillo */
  font-family: 'Montserrat', sans-serif;
  margin-bottom: 10px;
`;

const AppBody = styled.div`
  margin-top: 300px; /* Para que el contenido no se superponga con el encabezado y navbar */
  padding: 20px;
  background: linear-gradient(180deg, #2c2c2c, #000000); /* Degradado gris oscuro a negro */
  min-height: 100vh;
`;
async function obtenerDatos() {
  const response = await fetch("http://localhost:8080/public/orden/mostrarTodas", {
    method: "GET"
  });

  //convertir respuesta a json y array
  const data = await response.json();
  return data;
}

let ordenesArray;

obtenerDatos().then(data => {
  ordenesArray = data;
});

const App = () => {
  //console.log(ordenesArray);
  const orders = ordenesArray;

  const { user, isAuthenticated, isLoading, loginWithRedirect } = useAuth0();
  const [userId, setUserId] = useState(null);

  useEffect(() => {
    if (!isAuthenticated && !isLoading) {
      loginWithRedirect();  // Redirige al login si no está autenticado
      return;
    }
  }, [isAuthenticated, isLoading, loginWithRedirect]);
  useEffect(() => {
    try {
      isLoading = true;
      const cargarUsuario = async () => {
        const res = await fetch("http://localhost:8080/public/user/getUserId?userName=" + user.name, {
          method: "GET"
        });
        return res.json();
      }

      let id;
      cargarUsuario().then(data => {
        id = data;
      });
      if (user != null) {
        setUserId(() => id);
      }
    } catch (e) {
      console.error(e);
      return;
    } finally {
      isLoading = false;
    }
  }, [!isLoading, isAuthenticated != null]);

  if (isLoading) {
    return <div>Cargando...</div>;
  }

  return isAuthenticated && (
    <ThemeProvider theme={darkTheme}>
      <GlobalStyle />
      <Router>
        <HeaderContainer>
          <Logo src="/442418039_1441156863179431_493331331508367596_n.jpg" alt="Logo de Chepps" />
          <Title>Chepps</Title>
          {userId && (
            <Navbar userId={userId} />
          )};

        </HeaderContainer>
        <AppBody>
          <Routes>
            <Route path="/" element={<Navigate to="/ordenes" />} />
            <Route path="/ordenes" element={<OrdersTable orders={orders} />} />
            <Route path="/productos" element={<Products />} />
          </Routes>
        </AppBody>
      </Router>
    </ThemeProvider>


  );
};

export default App;
