import React, { useState, useEffect } from 'react';
import Modal from 'react-modal';
import styled from 'styled-components';
import { obtenerDatos } from '../helper/traeDatos';
import { convertirFechaConZonaHoraria } from '../helper/definirFecha';
import { ProductoDetalle } from './ProductoDetalle';
import Contacto from '../prototype/Contacto';
import Orden from '../prototype/Orden';

const Select = styled.select`
  width: 87%;
  padding: 10px;
  margin-bottom: 10px;
  margin-right: 10px;
  margin-left: 10px;
  background-color: #555; /* Fondo gris para los campos */
  color: #fff;
  border: none;
  border-radius: 4px;
  font-size: 16px;
  outline: none;

  &::placeholder {
    color: #ccc;
  }
`;
const ModalContent = styled.div`
  background: #333;
  padding: 20px;
  border-radius: 8px;
  color: #fff;
  max-width: 500px;
  width: 100%;
  height: auto;
  margin: 0 auto;
  overflow: hidden;
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
  gap: 15px;
  align-items: center;
`;

const Label = styled.label`
  width: 100%;
  color: #bbb;
  font-size: 14px;
`;

const Input = styled.input`
  width: 100%;
  padding: 10px;
  background-color: #555;
  color: #fff;
  border: none;
  border-radius: 4px;
  font-size: 16px;
  outline: none;

  &::placeholder {
    color: #ccc;
  }
`;

const Button = styled.button`
  background-color: #e0a800;
  color: #000;
  border: none;
  padding: 10px 20px;
  font-size: 16px;
  border-radius: 4px;
  cursor: pointer;
  margin-top: 10px;

  &:hover {
    background-color: #ffd700;
  }
`;

const ModalOrdenes = ({ isModalOpen, toggleModal, openConfirmationModal, userId, token }) => {

  const [sumaTotal, setSumaTotal] = useState(0);
  const [productos, setProductos] = useState(null);
  const [insumos, setInsumos] = useState([]);
  const [clientes, setClientes] = useState([]);
  const [detalles, setDetalles] = useState([]);
  const [selectedItem, setSelectedItem] = useState(null);
  const [pagado, setPagado] = useState(null);
  const [tipo, setTipo] = useState(null);
  const [total, setTotal] = useState(0);
  const [ordenDetalle, setOrdenDetalle] = useState([]);
  //console.log(userId);
  const [tipoPago, setTipoPago] = useState('EFECTIVO');

  const handleSelectChange = (e) => {
    const selectedItemid = parseInt(e.target.value, 10);
    const item = clientes.find((item) => item.idCliente === selectedItemid);
    setSelectedItem(item);

  };


  useEffect(() => {
    const cargarProductosYClientes = async () => {
      try {
        const url = "http://localhost:8080/public/productos";
        const urlClientes = "http://localhost:8080/public/clientes";

        const urlInsumos = "http://localhost:8080/public/insumo/retornarTodos";

        const dataProductos = await obtenerDatos(url, 'GET', token);
        const dataClientes = await obtenerDatos(urlClientes, 'GET', token);
        const dataInsumo = await obtenerDatos(urlInsumos, 'GET', token);
        setProductos(dataProductos);
        setClientes(dataClientes);
        setInsumos(dataInsumo);
      } catch (error) {
        console.error('Error al cargar los productos:', error);
      }
    };

    cargarProductosYClientes();
  }, [!productos]);

  // Funci√≥n para agregar un nuevo ProductoDetalle
  const handleAgregarDetalle = () => {
    if (productos.length > 0) {
      // Generamos un identificador √∫nico para cada detalle
      const nuevoDetalle = {
        id: Date.now(), // ID √∫nico
        productos,
        insumos,
        suma: 0, // Inicializamos la suma en 0
      };
      setDetalles([...detalles, nuevoDetalle]);
    } else {
      console.warn('Productos no disponibles para agregar.');
    }
  };

  // Funci√≥n para quitar el √∫ltimo ProductoDetalle
  const handleQuitarDetalle = () => {
    if (detalles.length > 0) {
      // Eliminar el √∫ltimo detalle y restar su suma del total
      const ultimoDetalle = detalles[detalles.length - 1];
      console.log(ultimoDetalle);
      const ultimaOrdenDetalle = ordenDetalle[detalles.length - 1];
      const descuento = (ultimaOrdenDetalle && ultimaOrdenDetalle.descuentosPorProducto) ? parseFloat(ultimaOrdenDetalle.descuentosPorProducto) : 0;
      const nuevaSuma = parseFloat(ultimoDetalle.suma) - descuento;
      console.log(nuevaSuma);
      //console.log(ultimoDetalle);
      //console.log(descuento);
      //console.log(ultimaOrdenDetalle);

      setSumaTotal((prevSumaTotal) => (prevSumaTotal - ultimoDetalle.suma).toFixed(2));
      setTotal((prevTotal) => prevTotal - nuevaSuma);
      setDetalles((prevDetalles) => prevDetalles.slice(0, -1));
      if (ordenDetalle.length === detalles.length) {
        setOrdenDetalle((prevOrdenDetalle) => prevOrdenDetalle.slice(0, -1));
      }
    }
  };

  const actualizarSumaDetalle = (id, nuevaSuma) => {
    setDetalles((prevDetalles) => {
      // Actualizamos el detalle espec√≠fico
      const nuevosDetalles = prevDetalles.map((detalle) =>
        detalle.id === id ?
          { ...detalle, suma: nuevaSuma } :
          detalle
      );

      // Recalculamos la suma total usando los nuevos detalles
      const nuevaSumaTotal = nuevosDetalles.reduce(
        (total, detalle) => total + detalle.suma,
        0
      );

      // Actualizamos la suma total
      setSumaTotal(nuevaSumaTotal);

      return nuevosDetalles; // Retornamos el nuevo estado de los detalles
    });
  };

  const enviarDatos = (event) => {
    event.preventDefault(); // Previene la recarga del formulario

    if (!selectedItem) {
      alert('Seleccione un cliente antes de guardar.');
      return;
    }
    if (!sumaTotal) {
      alert('Total de orden no puede ser 0');
      return;
    }
    if (detalles.length === 0) {
      alert('Agregue al menos un detalle.');
      return;
    }
    if (!pagado) {
      alert('Pagado debe contener un valor');
      return;
    }
    if (!tipo) {
      alert('Seleccione un tipo de orden');
      return;
    }

    // Capturar valores del formulario
    const form = event.target;
    const direccion = form.calle.value;
    const provincia = form.provincia.value;
    const telefono = form.telefono.value;
    const email = form.email.value;
    const fecha_entrega = (form.fecha.value) ? form.fecha.value : convertirFechaConZonaHoraria(new Date());

    const contacto = new Contacto({
      idContacto: null,
      telefono: telefono,
      email: email
    });

    const orden = new Orden({
      cliente: selectedItem,
      contacto: contacto,
      domicilio: { direccion, provincia },
      usuario: { idUsuario: userId },
      detalle: ordenDetalle,
      fecha_entrega,
      subTotal: sumaTotal,
      total: total,
      pagado,
      tipoOrden: tipo,
      tipoPago: tipoPago
    });
    const ordenRespuesta = cargarOrden(orden);

    if (ordenRespuesta) {
      form.provincia.value = "";
      form.telefono.value = "";
      form.calle.value = "";
      form.email.value = "";
      form.fecha.value = "";
      setSumaTotal(() => 0);
      setPagado(() => 0);
      setTotal(() => 0);
      setTipo(() => null);
      setDetalles(() => []);

      toggleModal();
      openConfirmationModal();

    } else {
      alert("Ha ocurrido un error!");
    }
  };

  async function cargarOrden(orden) {
    const url = 'http://localhost:8080/public/orden/guardar';

    const data = await obtenerDatos(url, 'POST', orden);
    return data;

  }


  function calcularDescuentos() {

    let descuentosPorProducto = 0;

    if (ordenDetalle.length > 0) {

      for (let i = 0; i < ordenDetalle.length; i++) {

        if (ordenDetalle[i].descuentosPorProducto > 0 && ordenDetalle[i].descuentosPorProducto <= sumaTotal) {
          descuentosPorProducto += parseFloat(ordenDetalle[i].descuentosPorProducto);
        }
      }
      descuentosPorProducto = sumaTotal - descuentosPorProducto;
      setTotal(() => descuentosPorProducto);
    }
  }
  return (
    <Modal isOpen={isModalOpen} onRequestClose={toggleModal} contentLabel="Formulario de Nueva Orden" style={{
      overlay: {
        background: "linear-gradient(135deg, #0a192f, #1f4068)", // üîπ Azul oscuro con degradado
      },
      content: {
        width: "50%",
        margin: "auto",
        padding: "20px",
        borderRadius: "8px",
        background: "black",
        border: "none"
      },
    }}>
      <ModalContent>
        <h2 style={{ textAlign: 'center' }}>Nueva Orden</h2>
        <Form onSubmit={enviarDatos}>
          <Label>Cliente</Label>
          <Select onChange={handleSelectChange}>
            <option>Seleccione uno...</option>
            {clientes.map((cliente) => (
              <option key={cliente.idCliente} value={cliente.idCliente}>{cliente.nombre}  {(cliente.apellido) ? cliente.apellido : ''}</option>
            ))}
          </Select>
          <div>
            <Label>Domicilio</Label>
            <Input type='text' name='calle' defaultValue="Domicilio generico." />
            <Label>Provincia</Label>
            <Input type='text' name='provincia' defaultValue="Mendoza" />
          </div>
          <div>
            <h3><span>Contacto</span></h3>
            <Label>Telefono:</Label>
            <Input type='tel' name='telefono' defaultValue="261" maxLength="10" />
            <Label>Email:</Label>
            <Input type='email' name='email' defaultValue="alguien@example.com" />
          </div>

          {
            detalles.map((detalle) => (
              <ProductoDetalle
                key={detalle.id}
                cont={detalles.length}
                contOrdenesDetalle={ordenDetalle.length}
                productos={detalle.productos}
                insumos={detalle.insumos}
                onActualizarSumaTotal={(nuevaSuma) => actualizarSumaDetalle(detalle.id, nuevaSuma)}
                setOrdenDetalle={setOrdenDetalle}
                calcularDescuentos={calcularDescuentos}
              />
            ))
          }
          <Button type="button" onClick={handleAgregarDetalle}>
            Agregar l√≠nea de producto
          </Button>
          <Button type="button" onClick={handleQuitarDetalle} disabled={detalles.length === 0}>
            Quitar √öltimo Detalle del Producto
          </Button>

          <Label>Fecha de Entrega:</Label>
          <Input type="datetime-local" name='fecha' />
          <Label>Tipo: </Label>
          <Select onChange={(event) => (setTipo(event.target.value))}>
            <option value={null}>Seleccione uno...</option>
            <option value={'PAGO'}>PAGO</option>
            <option value={'COMPRA'}>COMPRA</option>
            <option value={'VENTA'}>VENTA</option>
          </Select>

          <Label>Pagado:</Label>
          <Input type="number" placeholder="9.99" onChange={(event) => (setPagado(parseFloat(event.target.value)))} />
          <Label>tipo de pago:</Label>
          <Select onChange={(event) => (setTipoPago(event.target.value))}>
            <option value={'EFECTIVO'}>Efectivo</option>
            <option value={'TRANSFERENCIA'}>Transferencia</option>
            <option value={'VILLETERAS VIRTUALES (QR, LINK, ETC.)'}>VILLETERAS VIRTUALES (QR, LINK, ETC.)</option>
            <option value={'OTRO (cheques, pagar√©, cuenta corriente, etc.)'}>OTRO (cheques, pagar√©, cuenta corriente, etc.)</option>
          </Select>

          <h5>subTotal: <span>${sumaTotal}</span></h5>
          <h3>Total Orden: <span>${total}</span></h3>

          <Button type="submit">Enviar</Button>
          <Button type="button" onClick={toggleModal}>Cancelar</Button>
        </Form>
      </ModalContent>
    </Modal>
  );
};

export default ModalOrdenes;
