import React from 'react';
import Modal from 'react-modal';
import styled from 'styled-components';

const ModalContent = styled.div`
  background: #333;
  padding: 20px;
  border-radius: 8px;
  color: #fff;
  max-width: 500px;
  width: 100%;
  height: auto;
  margin: 0 auto;
`;

const Button = styled.button`
  background-color: ${({ theme }) => theme.primaryButton};
  color: #000;
  border: none;
  padding: 10px 15px;
  font-size: 16px;
  cursor: pointer;

  &:hover {
    background-color: #e0a800;
  }
`;

const ConfirmationModal = ({ isOpen, onClose, label, updateStatus }) => {
  const handlerClose = () => {
    updateStatus();
    onClose();

  }
  return (
    <Modal isOpen={isOpen} onRequestClose={onClose} contentLabel="√âxito" style={{
      overlay: {
        background: "linear-gradient(135deg, #0a192f, #1f4068)", // üîπ Azul oscuro con degradado
      },
      content: {
        width: "50%",
        margin: "auto",
        padding: "20px",
        borderRadius: "8px",
        background: "black",
        border: "none"
      },
    }}>
      <ModalContent>
        <h2>{label == null ? "Orden a√±adida exitosamente" : label}</h2>
        <Button type="button" onClick={handlerClose}>Cerrar</Button>
      </ModalContent>
    </Modal>
  )
}

export default ConfirmationModal;
