import React, { useState } from 'react';
import Modal from 'react-modal';
import styled from 'styled-components';
import { obtenerDatos } from '../helper/traeDatos';
import ProductoDetalle from './ProductoDetalle';

const ModalContent = styled.div`
  background: #333;
  padding: 20px;
  border-radius: 8px;
  color: #fff;
  max-width: 500px;
  width: 100%;
  height: auto;
  margin: 0 auto;
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
  gap: 15px; /* Espacio entre los campos */
  align-items: center;
`;

const Label = styled.label`
  width: 100%;
  color: #bbb;
  font-size: 14px;
`;

const Input = styled.input`
  width: 100%;
  padding: 10px;
  background-color: #555; /* Fondo gris para los campos */
  color: #fff; /* Letras blancas */
  border: none;
  border-radius: 4px;
  font-size: 16px;
  outline: none;

  &::placeholder {
    color: #ccc;
  }
`;
const Button = styled.button`
  background-color: #e0a800; /* Fondo amarillo */
  color: #000; /* Letras negras */
  border: none;
  padding: 10px 20px;
  font-size: 16px;
  border-radius: 4px;
  cursor: pointer;
  margin-top: 10px;

  &:hover {
    background-color: #ffd700; /* Cambia el color al pasar el mouse */
  }
`;
const url = "http://localhost:8080/public/productos";
const productos = await obtenerDatos(url, 'GET').then((data) => {
  return data;
});

const ModalOrdenes = ({ isModalOpen, toggleModal }) => {
  const [selectedItem, setSelectedItem] = useState(null);
  const handleSelectChange = (e) => {
    const selectedItemid = parseInt(e.target.value, 10);
    const item = productos.find((item) => item.idProducto === selectedItemid);
    setSelectedItem(item);

  };
  const enviarDatos = () => {
    if (selectedItem) {
      return null;
    }
  }
  return productos && (
    <Modal isOpen={isModalOpen} onRequestClose={toggleModal} contentLabel="Formulario de Nueva Orden">
      <ModalContent>
        <h2 style={{ textAlign: 'center' }}>Nueva Orden</h2>
        <Form>
          <ProductoDetalle />
          <Label>Fecha de Carga:</Label>
          <Input type="datetime-local" />

          <Label>Fecha de Entrega:</Label>
          <Input type="datetime-local" />

          <Label>Subtotal:</Label>
          <Input type="number" placeholder="5000.0" />

          <Label>Total:</Label>
          <Input type="number" placeholder="5500.0" />

          <Label>Pagado:</Label>
          <Input type="number" placeholder="" />

          <Button type="submit" onCanPlay={enviarDatos}>Guardar</Button>
          <Button type="button" onClick={toggleModal}>Cancelar</Button>
        </Form>
      </ModalContent>
    </Modal>
  );
};

export default ModalOrdenes;
