import { createGlobalStyle } from 'styled-components';

const GlobalStyle = createGlobalStyle`
  body {
    background-color: ${({ theme }) => theme.background}; /* Fondo general oscuro */
    color: ${({ theme }) => theme.text}; /* Texto blanco */
    font-family: 'Montserrat', sans-serif;
    margin: 0;
    padding: 0;
  }

  h1, h2, h3, h4, h5, h6 {
    color: ${({ theme }) => theme.text};
  }

  button {
    font-family: 'Montserrat', sans-serif;
  }


  .ReactModal__Overlay {
  z-index: 9999;
}

.ReactModal__Content {
  position: relative;
  background: #333;
  padding: 20px;
  border-radius: 8px;
  color: #fff;
  max-width: 500px;
  width: 90%;
  max-height: 80vh;
  overflow-y: auto;
}
`;

export default GlobalStyle;
