import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import { ThemeProvider } from 'styled-components';
import Navbar from './components/Navbar';
import OrdersTable from './components/OrdersTable';
import Products from './components/Products';
import { darkTheme } from './components/theme';
import GlobalStyle from './globalStyles';
import styled from 'styled-components';
import { useAuth0 } from '@auth0/auth0-react';

const HeaderContainer = styled.header`
  background-color: ${({ theme }) => theme.background};
  position: fixed;
  top: 0;
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.1);
  z-index: 1000;
  padding: 20px 0;
`;

const Logo = styled.img`
  height: 120px;
  margin-bottom: 10px;
`;

const Title = styled.h1`
  font-size: 36px;
  color: ${({ theme }) => theme.primaryButton}; /* Texto amarillo */
  font-family: 'Montserrat', sans-serif;
  margin-bottom: 10px;
`;

const AppBody = styled.div`
  margin-top: 250px; /* Para que el contenido no se superponga con el encabezado y navbar */
  padding: 20px;
  background: linear-gradient(180deg, #2c2c2c, #000000); /* Degradado gris oscuro a negro */
  min-height: 100vh;
`;

function login() {
  const { loginWithRedirect } = useAuth0();

  useEffect(() => {
    loginWithRedirect();
  }, [loginWithRedirect]);
  return null;
}

const App = () => {
  const [orders, setOrders] = useState([
    { id: 1, name: 'Orden 1', description: 'Detalles de la orden 1' },
    { id: 2, name: 'Orden 2', description: 'Detalles de la orden 2' }
  ]);

  const handleDeleteOrder = (id) => {
    setOrders(orders.filter((order) => order.id !== id));
  };
  const { isAuthenticated, isLoading } = useAuth0();
  if(isLoading){
    return <div>Cargando...</div>;
  }

  return isAuthenticated ? (  
    <ThemeProvider theme={darkTheme}>
      <GlobalStyle />
      <Router>
        <HeaderContainer>
          <Logo src="ruta/a/imagen.png" alt="Logo de Chepps" />
          <Title>Chepps</Title>
          <Navbar />

        </HeaderContainer>
        <AppBody>
          <Routes>
            <Route path="/" element={<Navigate to="/ordenes" />} />
            <Route path="/ordenes" element={<OrdersTable orders={orders} handleDelete={handleDeleteOrder} />} />
            <Route path="/productos" element={<Products />} /> 
          </Routes>
        </AppBody>
      </Router>
    </ThemeProvider>


  ) : (<login/> );
};

export default App;
