import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import { ThemeProvider } from 'styled-components';
import Navbar from './components/Navbar';
import Home from './components/Home';
import OrdersTable from './components/OrdersTable';
import { darkTheme } from './components/theme';
import GlobalStyle from './globalStyles';
import styled from 'styled-components';

const HeaderContainer = styled.header`
  background-color: ${({ theme }) => theme.background};
  position: fixed;
  top: 0;
  width: 100%;
  height: 150px;
  display: flex;
  justify-content: center;
  align-items: center;
  box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.1);
  z-index: 1000;
  padding: 20px;
`;

const Logo = styled.img`
  height: 120px;
  margin-right: 20px; /* Espacio entre la imagen y el texto */
`;

const Title = styled.h1`
  font-size: 36px;
  color: ${({ theme }) => theme.primaryButton}; /* Texto en color amarillo */
  font-family: 'Montserrat', sans-serif;
`;

const AppBody = styled.div`
  margin-top: 150px; /* Para que el contenido no se superponga con el encabezado */
  padding: 20px;
  background: linear-gradient(180deg, #2c2c2c, #000000); /* Degradado gris oscuro a negro */
  min-height: 100vh;
`;

const App = () => {
  const [orders, setOrders] = useState([
    { id: 1, name: 'Orden 1', description: 'Detalles de la orden 1' },
    { id: 2, name: 'Orden 2', description: 'Detalles de la orden 2' },
  ]);

  const handleDelete = (id) => {
    setOrders(orders.filter((order) => order.id !== id));
  };

  return (
    <ThemeProvider theme={darkTheme}>
      <GlobalStyle />
      <Router>
        <HeaderContainer>
          <Logo src="ruta/a/imagen.png" alt="Logo de Chepps" />
          <Title>Chepps</Title>
        </HeaderContainer>
        <Navbar />
        <AppBody>
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/ordenes" element={<OrdersTable orders={orders} handleDelete={handleDelete} />} />
          </Routes>
        </AppBody>
      </Router>
    </ThemeProvider>
  );
};

export default App;
