import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { obtenerDatos } from '../helper/traeDatos';
import { mostrarUnaOrden } from '../helper/url';

const OrderDetail = () => {
    const { id } = useParams();
    const [orden, setOrden] = useState(null);

    useEffect(() => {
        async function fetchOrden() {
            const datos = await obtenerDatos(mostrarUnaOrden(id), 'GET');
            setOrden(datos);
        }
        fetchOrden();
    }, [id]);

    if (!orden) return <div style={{ padding: '2rem', textAlign: 'center' }}>Cargando detalle...</div>;

    const fechaCarga = new Date(orden.fecha_carga);
    const fechaEntrega = new Date(orden.fecha_entrega);

    const formatearHora = (fecha) => {
        return fecha.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
    };

    const formatearFecha = (fecha) => {
        return fecha.toLocaleDateString();
    };

    const fechasIguales = fechaCarga.toDateString() === fechaEntrega.toDateString();

    return (
        <div style={{
            maxWidth: '900px',
            margin: '0 auto',
            padding: '2rem',
            fontFamily: 'Arial, sans-serif'
        }}>
            <h1 style={{ color: 'red', textAlign: 'center' }}>
                {orden.cliente.nombre} {orden.cliente.apellido}
            </h1>

            <div style={{ marginBottom: '1rem' }}>
                <p><strong>ID Orden:</strong> #{orden.idOrden}</p>
                <p><strong>Tipo Orden:</strong> {orden.tipoOrden}</p>
                <p><strong>Tipo de Pago:</strong> {orden.tipoPago}</p>
                <p><strong>Estado:</strong> {orden.estado}</p>
                <p><strong>Subtotal:</strong> ${orden.subTotal.toFixed(2)}</p>
                <p><strong>Total:</strong> ${orden.total.toFixed(2)}</p>
                <p><strong>Pagado:</strong> ${orden.pagado.toFixed(2)}</p>
            </div>

            <div style={{ marginBottom: '1rem' }}>
                <h3>Contacto</h3>
                <p><strong>Teléfono:</strong> {orden.contacto.telefono}</p>
                <p><strong>Email:</strong> {orden.contacto.email}</p>
            </div>

            <div style={{ marginBottom: '1rem' }}>
                <h3>Domicilio</h3>
                <p><strong>Dirección:</strong> {orden.domicilio.direccion}</p>
                <p><strong>Provincia:</strong> {orden.domicilio.provincia}</p>
            </div>

            <div style={{ marginBottom: '1rem' }}>
                <h3>Usuario</h3>
                <p><strong>Usuario:</strong> {orden.usuario.nombreUsuario}</p>
                <p><strong>Rol:</strong> {orden.usuario.role}</p>
            </div>

            <div style={{ marginBottom: '1rem', color: '#0066cc' }}>
                {fechasIguales ? (
                    <p><strong>Fecha de entrega y carga:</strong> hoy a las {formatearHora(fechaCarga)}</p>
                ) : (
                    <>
                        <p><strong>Fecha de carga:</strong> {formatearFecha(fechaCarga)} - {formatearHora(fechaCarga)}</p>
                        <p><strong>Fecha de entrega:</strong> {formatearFecha(fechaEntrega)} - {formatearHora(fechaEntrega)}</p>
                    </>
                )}
            </div>

            <div>
                <h3>Productos</h3>
                <div style={{ overflowX: 'auto' }}>
                    <table style={{
                        width: '100%',
                        borderCollapse: 'collapse',
                        marginTop: '1rem',
                        backgroundColor: '#f9f9f9'
                    }}>
                        <thead style={{ backgroundColor: '#333', color: '#fff' }}>
                            <tr>
                                <th style={cellStyle}>Producto</th>
                                <th style={cellStyle}>Descripción</th>
                                <th style={cellStyle}>Cantidad</th>
                                <th style={cellStyle}>Precio</th>
                                <th style={cellStyle}>Descuento</th>
                                <th style={cellStyle}>Observaciones</th>
                            </tr>
                        </thead>
                        <tbody>
                            {orden.detalle.map((item, index) => (
                                <tr key={index}>
                                    <td style={cellStyle}>{item.productos.denominacion}</td>
                                    <td style={cellStyle}>{item.productos.descripcion}</td>
                                    <td style={cellStyle}>{item.cantidadProducto}</td>
                                    <td style={cellStyle}>${item.productos.precio.toFixed(2)}</td>
                                    <td style={cellStyle}>{item.descuentosPorProducto}%</td>
                                    <td style={cellStyle}>{item.observaciones}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </div>
        </div>
    );
};

const cellStyle = {
    padding: '10px',
    border: '1px solid #ddd',
    textAlign: 'left'
};

export default OrderDetail;
