import React from "react";
import styled from 'styled-components';

const Select = styled.select`
  width: 50%;
  padding: 10px;
  margin-bottom: 10px;
  margin-right: 10px;
  background-color: #555; /* Fondo gris para los campos */
  color: #fff;
  border: none;
  border-radius: 4px;
  font-size: 16px;
  outline: none;

  &::placeholder {
    color: #ccc;
  }
`;
const CajaProducto = styled.div`
  display: inline-block;
  float: left;
  padding: 10px;
  border: #333;
  border-radius: 3px;
`;

const Button = styled.button`
  background-color: #e0a800; /* Fondo amarillo */
  color: #000; /* Letras negras */
  border: none;
  padding: 10px 20px;
  font-size: 16px;
  border-radius: 4px;
  cursor: pointer;
  margin-top: 10px;

  &:hover {
    background-color: #ffd700; /* Cambia el color al pasar el mouse */
  }
`;

const Number = styled.input`
  width: 80%;
  padding: 10px;
  background-color: #555; /* Fondo gris para los campos */
  color: #fff; /* Letras blancas */
  border: none;
  border-radius: 4px;
  font-size: 16px;
  outline: none;

  &::placeholder {
    color: #ccc;
  }
    `;
const Label = styled.label`
    width: 100%;
    color: #bbb;
    font-size: 14px;
  `;
export const ProductoDetalle = () => {
    return (
        <CajaProducto>
            <Label>Detalle</Label>
            <Select onChange={handleSelectChange}>
                <option value="">Seleccionar uno...</option>
                {productos.map((item) => (
                    <option key={item.idProductoManufacturado} value={item.idProductoManufacturado}>
                        {item.denominacion}
                    </option>
                ))}
            </Select>
            <Label>Cantidad:</Label>
            <Number type='number' className='cant' />
            <Button>Agregar linea de producto</Button>
        </CajaProducto>
    )
};
