import React, { useState, useEffect } from "react";
import { ModalContent, Form, Label, Input, Button, Select } from './ModalStyles';
import Modal from 'react-modal';
import SelectInsumo from './selectInsumo';
import { obtenerDatos } from '../helper/traeDatos';
import ProductoManufacturado from '../prototype/ProductoManufacturado';
import Historiial from '../prototype/Historial';
import ProductoDetalle from '../prototype/ProductoDetalle';
import UnidadMedida from '../prototype/UnidadMedida';
import Historial from '../prototype/Historial';

const ModalProductos = ({ isModalOpen, toggleModal, openConfirmationModal }) => {

    const [selected, setSelectedOption] = useState('No')
    const [insumos, setInsumos] = useState(null);
    const [compInsumo, setCompInsumo] = useState([]);
    const [itemInsumo, setItemInsumo] = useState([]);
    const [unidades, setUnidades] = useState(null);
    const [denominacion, setDenominacion] = useState(null);
    const [descripcion, setDescripcion] = useState(null);
    const [precio, setPrecio] = useState(0);
    const [stockActual, setStockActual] = useState(0);
    const [stockMinimo, setStockMinimo] = useState(0);
    const [unidad, setUnidad] = useState(null);
    const [tiempo_estimado, setTiempoEstimado] = useState();
    const [esParaElaborar, setElaborarOption] = useState(null);


    useEffect(() => {
        const urlInsumos = "http://localhost:8080/public/insumo/retornarTodos";
        const urlUnidades = "http://localhost:8080/public/unidad/obtenerUnidades";

        async function cargar() {
            try {
                const dataInsumo = await obtenerDatos(urlInsumos, 'GET');
                const dataUnidades = await obtenerDatos(urlUnidades, 'GET');
                setInsumos(dataInsumo);
                setUnidades(dataUnidades);
            } catch (e) {
                console.error(e);
            }
        }
        cargar();
    }, [isModalOpen == true])

    const handleSelectChange = (e) => {
        const option = e.target.value;
        setSelectedOption(option);
    }
    const handleSelectChangeElaborar = (e) => {
        const option = e.target.value;
        setElaborarOption(option);
    }
    function addInsumo() {
        if (insumos.length > 0) {

            const nuevoInsumoComp = {
                id: Date.now(),
                insumos
            };
            setCompInsumo(() => [...compInsumo, nuevoInsumoComp]);
        } else {
            alert("no hay insumos para mostrar.");
        }
    }
    function addInsumoItem(item) {
        if (insumos.length > 0) {
            setItemInsumo(() => [...itemInsumo, item]);
        }
    }
    const handlerStock = (e) => {
        const stock = parseFloat(e.target.value);
        if (stock < stockMinimo) {
            alert("stock no puede ser menor a " + stockMinimo);
            e.target.value = "";
            return;
        }
        setStockActual(stock);
    }

    function cargarProducto() {
        const producto = {
            denominacion,
            idUnidadMedida: unidad,
            insumos: itemInsumo,
            descripcion,
            stockActual,
            stockMinimo,
            tiempo_estimado,
            precio
        }
        if (selected == 'No') {
            let ins = [];
            let historial = [];

            for (let i = 0; i < producto.insumos.length; i++) {

                ins = [...ins, producto.insumos[i].select];
                historial = [...historial, new Historial(null, producto.insumos[i].select, producto.insumos[i].cantidad, "")];
            }
            const detalle = new ProductoDetalle(null, producto.stockActual, producto.stockMinimo);
            const ProductoMan = new ProductoManufacturado(producto.denominacion, producto.idUnidadMedida, detalle, ins, historial, producto.precio, 0, producto.descripcion, producto.tiempo_estimado, false);

            console.log(ProductoMan);

        }
    }
    return (
        <Modal isOpen={isModalOpen} onRequestClose={toggleModal} contentLabel="Formulario de Nueva Orden" style={{
            overlay: {
                background: "linear-gradient(135deg, #0a192f,rgb(62, 71, 83))", // ðŸ”¹ Azul oscuro con degradado
            },
            content: {
                width: "50%",
                margin: "auto",
                padding: "20px",
                borderRadius: "8px",
                background: "black",
                border: "none"
            },
        }}>
            <ModalContent>
                <h1>Nuevo producto</h1>
                <hr />
                <Form>

                    <Label>Denominacion:</Label>
                    <Input type="text" required onChange={(e) => (setDenominacion(e.target.value))} />
                    {unidades && (
                        <>
                            <Label>Unidad de medida:</Label>
                            <Select onChange={(e) => (setUnidad(e.target.value))}>
                                <option>Seleccionar uno...</option>
                                {unidades.map((unidad) => (
                                    <option key={unidad.idUnidadMedida} value={unidad.idUnidadMedida}>{unidad.denominacion}</option>
                                ))}
                            </Select>
                        </>
                    )}
                    <h3>Detalles del producto:</h3>
                    <Label>Â¿Es un producto insumo?</Label>
                    <Select onChange={handleSelectChange}>
                        <option value={'No'}>No</option>
                        <option value={'Si'}>Si</option>
                    </Select>
                    {
                        selected == 'No' ? (
                            <>
                                <h3>Producto manufacturado</h3>
                                <Button type="button" onClick={addInsumo}>Agregar insumo</Button>
                                {compInsumo.map((comp) => (
                                    <SelectInsumo key={comp.id}
                                        insumos={insumos} addInsumoItem={addInsumoItem} />
                                ))}
                            </>
                        ) : (
                            <>
                                <h3>Producto/insumo</h3>
                                <Label>Â¿es para elaborar?</Label>
                                <Select onChange={handleSelectChangeElaborar}>
                                    <option value={'No'}>No</option>
                                    <option value={'Si'}>Si</option>
                                </Select>
                            </>
                        )
                    }
                    <Label>Stock minimo:</Label>
                    <Input type="number" onChange={(e) => (setStockMinimo(e.target.value))} />
                    <Label>Stock actual:</Label>
                    <Input type="number" onChange={handlerStock} />
                    <Label>Descripcion:</Label>
                    <Input type="text" onChange={(e) => (setDescripcion(e.target.value))} />
                    <Label>Tiempo estimado: {"(Opcional)"} </Label>
                    <Input type="datetime-local" onChange={(e) => (setTiempoEstimado(e.target.value))} />
                    <Label>Precio:</Label>
                    <Input type="number" onChange={(e) => (setPrecio(e.target.value))} />
                    <Button type="button" onClick={cargarProducto}>Aceptar</Button>
                    <Button>Cancelar</Button>
                </Form>
            </ModalContent>
        </Modal>
    )
}

export default ModalProductos
