import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import LogoutButton from './LogoutButton';
import ModalOrdenes from './modalOrdenes';
import ConfirmationModal from './ModalConfirm'

const NavbarContainer = styled.nav`
  display: flex;
  justify-content: space-around; /* Espaciado entre los elementos */
  align-items: center;
  background-color: ${({ theme }) => theme.navbarBackground};
  padding: 10px;
  width: 100%;
  box-shadow: 0px 2px 4px rgba(0, 0, 0, 0.1);
`;

const NavbarItem = styled(Link)`
  color: #fff;
  text-decoration: none;
  padding: 10px 20px;
  font-size: 18px;

  &:hover {
    color: ${({ theme }) => theme.primaryButton}; /* Color amarillo al pasar el mouse */
  }
`;

const Button = styled.button`
  background-color: ${({ theme }) => theme.primaryButton};
  color: #000; /* Color del texto */
  border: none;
  padding: 10px 15px;
  font-size: 16px;
  cursor: pointer;
  margin-left: 20px; /* Espaciado entre el botón y la navbar */

  &:hover {
    background-color: #e0a800;
  }
`;

const Navbar = ({ userId }) => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isConfirmationModalOpen, setIsConfirmationModalOpen] = useState(false);

  const toggleModal = () => setIsModalOpen(!isModalOpen);

  const openConfirmationModal = () => setIsConfirmationModalOpen(true);

  const closeConfirmationModal = () => setIsConfirmationModalOpen(false);

  return (
    <NavbarContainer>
      <NavbarItem to="/productos">Productos</NavbarItem>
      <NavbarItem to="/ordenes">Ordenes</NavbarItem>
      <NavbarItem>Generacion de reportes</NavbarItem>
      <Button onClick={toggleModal}>Nueva Orden</Button>
      <Button onClick={() => alert('Agregar Producto')}>Agregar Producto</Button>
      <LogoutButton />
      <ModalOrdenes isModalOpen={isModalOpen} toggleModal={toggleModal} openConfirmationModal={openConfirmationModal} userId={userId} style={{
        overlay: {
          backgroundColor: "transparent"
        }
      }} />

      <ConfirmationModal
        isOpen={isConfirmationModalOpen}
        onClose={closeConfirmationModal}
      />

    </NavbarContainer>
  );
};

export default Navbar;
