import { useEffect, useState } from 'react';
import { obtenerDatos } from '../helper/traeDatos';
import { insumoFindById, manufacturadoFiindById } from '../helper/url';
import { useParams, useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import { Label } from './ModalStyles';
import { obtenerHistorial, actualizarProducto } from '../helper/url';

export const ButtonHiellow = styled.button`
  background-color: #e0a800;
  color: #000;
  border: none;
  padding: 10px 20px;
  font-size: 16px;
  border-radius: 4px;
  cursor: pointer;
  margin-top: 10px;

  &:hover {
    background-color: #ffd700;
  }
`;

const Container = styled.div`
  max-width: 800px;
  margin: auto;
  padding: 20px;
  background-color: #222;
  color: #fff;
  border-radius: 10px;

  @media (max-width: 600px) {
    padding: 15px;
  }
`;

const Title = styled.h1`
  font-size: 1.8rem;
  margin-bottom: 20px;
`;

const FieldGroup = styled.div`
  display: flex;
  flex-direction: column;
  margin-bottom: 15px;

  label {
    margin-bottom: 5px;
    font-weight: bold;
  }

  input {
    padding: 10px;
    border-radius: 6px;
    border: none;
    background-color: #333;
    color: #fff;
    font-size: 1rem;

    &:focus {
      outline: 2px solid #555;
    }
  }
`;
const TableWrapper = styled.div`
  display: none;

  @media (min-width: 768px) {
    display: block;
    overflow: hidden;
    border-radius: 0.5rem;
    border: 1px solid rgba(23, 49, 58, 0.4);
  }
`;

const StyledTable = styled.table`
  width: 100%;
  table-layout: fixed;
  text-align: left;
`;

const Thead = styled.thead`
  background-color: #071017;
  border-bottom: 1px solid #17313a;
`;

const Th = styled.th`
  padding: 0.75rem 1rem;
  font-size: 0.875rem;
  font-weight: 500;
  text-transform: uppercase;
  letter-spacing: 0.05em;
  color: #67e8f9; /* cyan-300 */
`;

const Tbody = styled.tbody`
  background-color: #071217;
`;

const Tr = styled.tr`
  border-bottom: 1px solid #17313a;

  &:last-child {
    border-bottom: none;
  }

  &:hover {
    background-color: #092127;
  }
`;

const Td = styled.td`
  padding: 0.75rem 1rem;
  font-size: 0.875rem;
  font-weight: ${({ bold }) => (bold ? "500" : "400")};
  opacity: ${({ faded }) => (faded ? "0.95" : "1")};
`;

const MobileGrid = styled.div`
  display: grid;
  gap: 0.75rem;

  @media (min-width: 768px) {
    display: none;
  }
`;

const Card = styled.article`
  padding: 0.75rem;
  border-radius: 0.75rem;
  border: 1px solid #17313a;
  background: linear-gradient(to bottom, #061019, #071217);
  box-shadow: 0 1px 2px rgba(0, 0, 0, 0.3);
`;

const CardHeader = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
`;

const CardTitle = styled.h3`
  font-size: 0.875rem;
  font-weight: 600;
  color: white;
`;

const CardText = styled.p`
  margin-top: 0.5rem;
  font-size: 0.75rem;
  opacity: 0.9;

  span {
    font-weight: 500;
  }
`;

const ProductoDetalleInfo = ({ token, manufacturado = false }) => {
    const { id } = useParams();
    const navigate = useNavigate();
    const [producto, setProducto] = useState(null);
    const [denominacion, setDenom] = useState('');
    const [precio, setPrecio] = useState(0);
    const [botonEstado, setBotonEstado] = useState(false);
    const [historial, setHistorial] = useState();

    useEffect(() => {
        async function cargar() {
            if (id != null && token) {
                const url = (!manufacturado) ? insumoFindById(id) : manufacturadoFiindById(id);
                const data = await obtenerDatos(url, 'GET', token);
                setProducto(data);
            }
        }
        cargar();
    }, [id, token]);
    useEffect(() => {
        if (!producto) {
            return;
        }
        if (precio != producto.precio && precio != 0 || denominacion != producto.denominacion && denominacion != '') {
            setBotonEstado(true);
            return;
        }
        if (manufacturado && historial == null) {
            historialProducto();
        }
        setPrecio(producto.precio);
        setDenom(producto.denominacion);
        setBotonEstado(false);
    }, [producto]);

    const actualizar = async () => {
        if (manufacturado) {
            historial.map((h) => {
                h.nombreProducto = producto.denominacion;
            });
            producto.historial = historial;
        }
        const url = actualizarProducto(manufacturado);
        const datos = obtenerDatos(url, 'PUT', token, producto);
        setHistorial([]);
        setDenom('');
        setPrecio(0);
        setProducto(null);
        setBotonEstado(false);
        navigate('/');
        return;
    }
    async function historialProducto() {
        const urlHistorial = obtenerHistorial(producto.idProductoManufacturado);
        const dataHistorial = await obtenerDatos(urlHistorial, 'GET', token);
        setHistorial(dataHistorial);
    }

    if (!producto || producto.eliminado == true) {
        return <h2>Cargando...</h2>;
    }

    return (
        <Container>
            <Title>
                Detalle de producto #
                {!manufacturado ? producto.idInsumo : producto.idProductoManufacturado}
            </Title>

            <FieldGroup>
                <label>Denominación</label>
                <input
                    type="text"
                    value={producto.denominacion || ""}
                    onChange={(e) =>
                        setProducto({ ...producto, denominacion: e.target.value })
                    }
                />
            </FieldGroup>

            <FieldGroup>
                <label>Precio</label>
                <input
                    type="number"
                    value={producto.precio || ""}
                    onChange={(e) =>
                        setProducto({ ...producto, precio: e.target.value })
                    }
                    step="0.01"
                    onWheel={(e) => e.target.blur()}
                />
            </FieldGroup>

            {!manufacturado ? (
                <>
                    <FieldGroup>
                        <label>Es para elaborar</label>
                        <input
                            type="checkbox"
                            checked={producto.esParaElaborar || false}
                            disabled
                        />
                    </FieldGroup>

                    <FieldGroup>
                        <label>Marca</label>
                        {producto.marca?.nombre ? (
                            <h3>{producto.marca.nombre}</h3>
                        ) : (
                            <h3>Sin marca</h3>
                        )}
                    </FieldGroup>
                </>
            ) : (
                <>
                    {producto.tiempo_estimado != null && (
                        <FieldGroup>
                            <Label>Tiempo estimado:</Label>
                            <h3>{producto.tiempo_estimado}</h3>
                        </FieldGroup>
                    )}

                    {producto.descripcion && (
                        <FieldGroup>
                            <label>Descripción:</label>
                            <h3>
                                <strong>{producto.descripcion}</strong>
                            </h3>
                        </FieldGroup>
                    )}

                    {Array.isArray(producto.insumos) && producto.insumos.length > 0 && (
                        <div className="min-h-0 w-full p-4 sm:p-6 bg-[#0b0f13] text-white rounded-2xl shadow-lg">
                            <header className="flex items-center justify-between mb-4">
                                <h2 className="text-lg sm:text-xl font-semibold tracking-wide">
                                    Ingredientes:
                                </h2>
                            </header>

                            {/* Desktop */}
                            <TableWrapper>
                                <StyledTable>
                                    <Thead>
                                        <tr>
                                            <Th>Nombre</Th>
                                            <Th>Marca</Th>
                                        </tr>
                                    </Thead>
                                    <Tbody>
                                        {producto.insumos.map((it, idx) => (
                                            <Tr key={it.id ?? idx}>
                                                <Td bold>{it.denominacion}</Td>
                                                <Td faded>{it.marca?.nombre}</Td>
                                            </Tr>
                                        ))}
                                    </Tbody>
                                </StyledTable>
                            </TableWrapper>

                            {/* Mobile */}
                            <MobileGrid>
                                {producto.insumos.map((it, idx) => (
                                    <Card key={it.idInsumo ?? idx}>
                                        <CardHeader>
                                            <CardTitle>{it.denominacion}</CardTitle>
                                        </CardHeader>
                                        <CardText>
                                            Marca: <span>{it.marca?.nombre}</span>
                                        </CardText>
                                        <CardText>
                                            Stock actual: <span>{it.detalle?.stockActual}</span>
                                        </CardText>
                                        <CardText>
                                            Unidad medida:{" "}
                                            <span>{it.unidadMedida?.denominacion}</span>
                                        </CardText>
                                    </Card>
                                ))}
                            </MobileGrid>
                        </div>
                    )}

                    <FieldGroup>
                        <label>Detalle (stock actual / mínimo)</label>
                        <input
                            type="text"
                            value={
                                producto.detalle
                                    ? `${producto.detalle.stockActual} / ${producto.detalle.stockMinimo}`
                                    : ""
                            }
                            readOnly
                        />
                    </FieldGroup>

                    {botonEstado && (
                        <ButtonHiellow onClick={actualizar}>Actualizar</ButtonHiellow>
                    )}
                </>
            )}
        </Container>
    );
}
export default ProductoDetalleInfo;
