import React, { useState } from "react";
import styled from 'styled-components';

const Input = styled.input`
  width: 100%;
  padding: 10px;
  background-color: #555;
  color: #fff;
  border: none;
  border-radius: 4px;
  font-size: 16px;
  outline: none;

  &::placeholder {
    color: #ccc;
  }
`;
const Button = styled.button`
  background-color: #e0a800;
  color: #000;
  border: none;
  padding: 10px 20px;
  font-size: 16px;
  border-radius: 4px;
  cursor: pointer;
  margin-top: 10px;

  &:hover {
    background-color: #ffd700;
  }
`;

const Select = styled.select`
  width: 87%;
  padding: 10px;
  margin-bottom: 10px;
  margin-right: 10px;
  margin-left: 10px;
  background-color: #555; /* Fondo gris para los campos */
  color: #fff;
  border: none;
  border-radius: 4px;
  font-size: 16px;
  outline: none;

  &::placeholder {
    color: #ccc;
  }
`;
const CajaProducto = styled.div`
  display: inline-block;
  float: left;
  padding: 10px;
  border: #333;
  border-radius: 3px;
`;

const Number = styled.input`
  width: 80%;
  padding: 10px;
  background-color: #555; /* Fondo gris para los campos */
  color: #fff; /* Letras blancas */
  border: none;
  border-radius: 4px;
  font-size: 16px;
  outline: none;

  &::placeholder {
    color: #ccc;
  }
    `;
const Label = styled.label`
    width: 100%;
    margin-right: 15px;
    color: #bbb;
    font-size: 14px;
  `;
export const ProductoDetalle = ({ cont, contOrdenesDetalle, productos, insumos, onActualizarSumaTotal, setOrdenDetalle }) => {

  const [selectedItemProducto, setSelectedItemProductos] = useState(null);

  const [selectedItemInsumo, setSelectedItemInsumo] = useState(null);
  const [sumaTotalProductos, setSumaTotalProductos] = useState(0);
  const [sumaTotalInsumos, setSumaTotalInsumos] = useState(0);
  const [sumaTotal, setSuma] = useState(0);
  const [cantidadProducto, setCantidadProducto] = useState(0);
  const [observaciones, setObservaciones] = useState("");
  const [descuentos, setDescuentos] = useState(0);

  const agregarDetalles = (ordenDetalle) => {
    if (cont != contOrdenesDetalle) {

      setOrdenDetalle((prevOrdenDetalle) => (!prevOrdenDetalle.some((item) => JSON.stringify(item) == JSON.stringify(ordenDetalle)))
        ? [...prevOrdenDetalle, ordenDetalle] : prevOrdenDetalle);
    }else {
      setOrdenDetalle((prevOrdenDetalle) => {
        const copia = [...prevOrdenDetalle];
        if(copia.length > 0) {
          copia[cont - 1] = ordenDetalle;
        }
        return copia;
      });
    }
  }
  const handleSelectChange = (e) => {
    const selectedItemid = parseInt(e.target.value, 10);
    const item = productos.find((item) => item.idProductoManufacturado === selectedItemid);
    setSelectedItemProductos(item);
  };
  const handleSelectChangeInsumo = (e) => {
    const selectedItemid = parseInt(e.target.value, 10);
    const item = insumos.find((item) => item.idInsumo === selectedItemid);
    setSelectedItemInsumo(item);
  };
  const handleInput = (e) => {
    const cantidad = e.target.value;
    let precio = selectedItemProducto.precio;
    let suma = (precio * cantidad);
    setSumaTotalProductos(suma);
    const agregarCantidadProducto = (cantidad) => {
      setCantidadProducto(cantidad)
    };
    agregarCantidadProducto(cantidad);
  };
  const handleInputInsumo = (e) => {
    const cantidad = e.target.value;
    let precio = selectedItemInsumo.precio;
    let suma = (precio * cantidad);
    setSumaTotalInsumos(suma);
  };

  const agregarObservaciones = (observaciones) => {
    setObservaciones(observaciones)
  };
  const agregarDescuentos = (descuentos) => {
    setDescuentos(descuentos);
  }
  function sumarTodo() {
    const nuevaSuma = sumaTotalProductos + sumaTotalInsumos;
    setSuma(nuevaSuma);
    onActualizarSumaTotal(nuevaSuma);

    const ordenDetalle = {
      'productos': { idProductoManufacturado: selectedItemProducto.idProductoManufacturado },
      'descuentosPorProducto': descuentos,
      'observaciones': observaciones,
      'cantidadProducto': cantidadProducto
    };
    agregarDetalles(ordenDetalle);
  }
  return (
    <CajaProducto>
      <h2><span>Detalle</span></h2>
      <Label>Productos:</Label>
      <Select onChange={handleSelectChange}>
        <option value="">Seleccionar uno...</option>
        {productos.map((item) => (
          <option key={item.idProductoManufacturado} value={item.idProductoManufacturado}>
            {item.denominacion}
          </option>
        ))}
      </Select>
      <Label>Otros productos: </Label>
      <Select onChange={handleSelectChangeInsumo}>
        <option value="">Seleccionar uno...</option>
        {insumos.map((item) => (
          <option key={item.idInsumo} value={item.idInsumo}>
            {item.denominacion}
          </option>
        ))}
      </Select>
      <Label>Descuentos por producto:</Label>
      <Number type='number' onChange={agregarDescuentos} />
      <Label>Observaciones: </Label>
      <Input type='text' onChange={agregarObservaciones} />
      {selectedItemProducto != null && (
        <div>
          <Label>Cantidad de productos:</Label>
          <Number type='number' onChange={handleInput} name="cantidad" />
          <h2>Insumos: </h2>
        </div>
      )}
      {selectedItemInsumo != null && (
        <div>
          <Label>Cantidad otros productos:</Label>
          <Number type='number' className="campoInsumo" onChange={handleInputInsumo} />
        </div>
      )}
      <Button type="button" onClick={sumarTodo}>Calcular</Button>
      <h4>Total: </h4>
      <h2>${sumaTotal}</h2>
      <hr />
    </CajaProducto>
  )
};
