import React, { useState } from "react";
import styled from 'styled-components';

const Input = styled.input`
  width: 100%;
  padding: 10px;
  background-color: #555;
  color: #fff;
  border: none;
  border-radius: 4px;
  font-size: 16px;
  outline: none;

  &::placeholder {
    color: #ccc;
  }
`;

const Select = styled.select`
  width: 87%;
  padding: 10px;
  margin-bottom: 10px;
  margin-right: 10px;
  margin-left: 10px;
  background-color: #555; /* Fondo gris para los campos */
  color: #fff;
  border: none;
  border-radius: 4px;
  font-size: 16px;
  outline: none;

  &::placeholder {
    color: #ccc;
  }
`;
const CajaProducto = styled.div`
  display: inline-block;
  float: left;
  padding: 10px;
  border: #333;
  border-radius: 3px;
`;

const Number = styled.input`
  width: 80%;
  padding: 10px;
  background-color: #555; /* Fondo gris para los campos */
  color: #fff; /* Letras blancas */
  border: none;
  border-radius: 4px;
  font-size: 16px;
  outline: none;

  &::placeholder {
    color: #ccc;
  }
    `;
const Label = styled.label`
    width: 100%;
    margin-right: 15px;
    color: #bbb;
    font-size: 14px;
  `;
export const ProductoDetalle = ({ productos }) => {
  const [selectedItem, setSelectedItem] = useState(null);
  const [suma, setSuma] = useState(0);

  const handleSelectChange = (e) => {
    const selectedItemid = parseInt(e.target.value, 10);
    const item = productos.find((item) => item.idProductoManufacturado === selectedItemid);
    setSelectedItem(item);

  };
  const handleInput = (e) => {
    const cantidad = e.target.value;
    let precio = selectedItem.precio;
    let suma = (precio * cantidad);
    console.log(selectedItem);
    setSuma(suma);
  };
  return (
    <CajaProducto>
      <Label>Detalle</Label>
      <Select onChange={handleSelectChange}>
        <option value="">Seleccionar uno...</option>
        {productos.map((item) => (
          <option key={item.idProductoManufacturado} value={item.idProductoManufacturado}>
            {item.denominacion}
          </option>
        ))}
      </Select>
      <Label>Descuentos por producto:</Label>
      <Number type='number' />
      <Label>Observaciones: </Label>
      <Input type='text' />
      <Label>Cantidad:</Label>
      <Number type='number' onChange={handleInput} />
      <h4>Total: </h4>
      <h2><span>${suma}</span></h2>
      <hr />
    </CajaProducto>
  )
};
