import React from 'react';
import styled from 'styled-components';

const TableContainer = styled.div`
  width: 100%;
  padding: 20px;
  background: linear-gradient(180deg, #2c2c2c, #000000); /* Degradado gris oscuro a negro */
  border-radius: 8px;
  box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.2);
`;

const Table = styled.table`
  width: 100%;
  border-collapse: collapse;
  color: ${({ theme }) => theme.text};
`;

const TableHead = styled.thead`
  background-color: #2c2c2c;
`;

const TableHeader = styled.th`
  padding: 15px;
  background-color: #3a3a3a; /* Gris oscuro para encabezados */
  color: ${({ theme }) => theme.text};
  text-align: left;
  border-bottom: 2px solid #000;
`;

const TableRow = styled.tr`
  &:nth-child(even) {
    background-color: #2c2c2c;
  }
  &:nth-child(odd) {
    background-color: #1b1b1b;
  }
`;

const TableData = styled.td`
  padding: 15px;
  border-bottom: 1px solid #000;
  color: ${({ theme }) => theme.text};
`;

const DeleteButton = styled.button`
  background-color: ${({ theme }) => theme.primaryButton};
  color: black;
  border: none;
  padding: 10px;
  cursor: pointer;
  border-radius: 5px;
  &:hover {
    background-color: ${({ theme }) => theme.primaryButtonHover};
  }
`;

const OrdersTable = ({ orders, handleDelete }) => {
  return (
    <TableContainer>
      <Table>
        <TableHead>
          <TableRow>
            <TableHeader>ID</TableHeader>
            <TableHeader>Nombre</TableHeader>
            <TableHeader>Descripci√≥n</TableHeader>
            <TableHeader>Acciones</TableHeader>
          </TableRow>
        </TableHead>
        <tbody>
          {orders.map((order) => (
            <TableRow key={order.id}>
              <TableData>{order.id}</TableData>
              <TableData>{order.name}</TableData>
              <TableData>{order.description}</TableData>
              <TableData>
                <DeleteButton onClick={() => handleDelete(order.id)}>Eliminar</DeleteButton>
              </TableData>
            </TableRow>
          ))}
        </tbody>
      </Table>
    </TableContainer>
  );
};

export default OrdersTable;
