import React, { useState, useEffect } from "react";
import { ModalContent, Form, Label, Input, Button, Select } from './ModalStyles';
import Modal from 'react-modal';
import SelectInsumo from './selectInsumo';
import { obtenerDatos } from '../helper/traeDatos';

const ModalProductos = ({ isModalOpen, toggleModal, openConfirmationModal }) => {
    const [selected, setSelectedOption] = useState('No')
    const [insumos, setInsumos] = useState(null);
    const [compInsumo, setCompInsumo] = useState([]);
    const [itemInsumo, setItemInsumo] = useState([]);
    const [unidades, setUnidades] = useState(null);

    const handleSelectChange = (e) => {
        const option = e.target.value;
        setSelectedOption(option);
    };

    useEffect(() => {
        const urlInsumos = "http://localhost:8080/public/insumo/retornarTodos";
        const urlUnidades = "http://localhost:8080/public/unidad/obtenerUnidades";

        async function cargar() {
            try {
                const dataInsumo = await obtenerDatos(urlInsumos, 'GET');
                const dataUnidades = await obtenerDatos(urlUnidades, 'GET');
                setInsumos(dataInsumo);
                setUnidades(dataUnidades);
            } catch (e) {
                console.error(e);
            }
        }
        cargar();
    }, [isModalOpen == true])

    function addInsumo() {
        if (insumos.length > 0) {

            const nuevoInsumoComp = {
                id: Date.now(),
                insumos
            };
            setCompInsumo(() => [...compInsumo, nuevoInsumoComp]);
        } else {
            alert("no hay insumos para mostrar.");
        }
    }
    function addInsumoItem(item) {
        if (insumos.length > 0) {
            setItemInsumo(() => [...itemInsumo, item]);
        }
    }
    return (
        <Modal isOpen={isModalOpen} onRequestClose={toggleModal} contentLabel="Formulario de Nueva Orden" style={{
            overlay: {
                background: "linear-gradient(135deg, #0a192f,rgb(62, 71, 83))", // ðŸ”¹ Azul oscuro con degradado
            },
            content: {
                width: "50%",
                margin: "auto",
                padding: "20px",
                borderRadius: "8px",
                background: "black",
                border: "none"
            },
        }}>
            <ModalContent>
                <h1>Nuevo producto</h1>
                <hr />
                <Form>

                    <Label>Denominacion:</Label>
                    <Input required />
                    {unidades && (
                        <>
                            <Label>Unidad de medida:</Label>
                            <Select>
                                <option>Seleccionar uno...</option>
                                {unidades.map((unidad) => (
                                    <option key={unidad.idUnidadMedida} value={unidad.idUnidadMedida}>{unidad.denominacion}</option>
                                ))}
                            </Select>
                        </>
                    )}
                    <h3>Detalles del producto:</h3>
                    <Label>Â¿Es un producto insumo?</Label>
                    <Select onChange={handleSelectChange}>
                        <option value={'No'}>No</option>
                        <option value={'Si'}>Si</option>
                    </Select>
                    {
                        selected == 'No' ? (
                            <>
                                <h3>Producto manufacturado</h3>
                                <Button type="button" onClick={addInsumo}>Agregar insumo</Button>
                                {compInsumo.map((comp) => (
                                    <SelectInsumo key={comp.id}
                                        insumos={insumos} addInsumoItem={addInsumoItem} />
                                ))}
                            </>
                        ) : (
                            <h3>Producto/insumo</h3>
                        )
                    }
                    <Label>Stock actual:</Label>
                    <Input type="number" />
                    <Label>Stock minimo:</Label>
                    <Input type="number" />
                    <Button>Aceptar</Button>
                    <Button>Cancelar</Button>
                </Form>
            </ModalContent>
        </Modal>
    )
}

export default ModalProductos
