import Modal from 'react-modal';
import styled from 'styled-components';

const Button = styled.button`
  background-color: #e0a800;
  color: #000;
  border: none;
  padding: 10px 20px;
  font-size: 16px;
  border-radius: 4px;
  cursor: pointer;
  margin-top: 10px;

  &:hover {
    background-color: #ffd700;
  }
`;
const ModalContent = styled.div`
  background: #333;
  padding: 20px;
  border-radius: 8px;
  color: #fff;
  max-width: 500px;
  width: 100%;
  height: auto;
  margin: 0 auto;
`;

const ConfirmationModal = ({ openConfirmationModal }) => (
    <Modal contentLabel="Éxito">
        <ModalContent>
            <h2>Orden añadida exitosamente</h2>
            <Button type="button" onClick={openConfirmationModal}>Cerrar</Button>
        </ModalContent>
    </Modal>
);
export default ConfirmationModal;
