import Modal from 'react-modal';
import styled from 'styled-components';

const ModalContent = styled.div`
  background: #333;
  padding: 20px;
  border-radius: 8px;
  color: #fff;
  max-width: 500px;
  width: 90%;
  margin: 0 auto;
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
  gap: 15px; /* Espacio entre los campos */
  align-items: center;
`;

const Label = styled.label`
  width: 100%;
  color: #bbb;
  font-size: 14px;
`;

const Input = styled.input`
  width: 100%;
  padding: 10px;
  background-color: #555; /* Fondo gris para los campos */
  color: #fff; /* Letras blancas */
  border: none;
  border-radius: 4px;
  font-size: 16px;
  outline: none;

  &::placeholder {
    color: #ccc;
  }
`;

const Button = styled.button`
  background-color: #e0a800; /* Fondo amarillo */
  color: #000; /* Letras negras */
  border: none;
  padding: 10px 20px;
  font-size: 16px;
  border-radius: 4px;
  cursor: pointer;
  margin-top: 10px;

  &:hover {
    background-color: #ffd700; /* Cambia el color al pasar el mouse */
  }
`;


const ModalOrdenes = ({ isModalOpen, toggleModal }) => {
    return (
        <Modal isOpen={isModalOpen} onRequestClose={toggleModal}>
            <ModalContent>
                <h2>Nueva Orden</h2>
                <form>
                    <label>Cliente ID:</label>
                    <input type="text" placeholder="1" />

                    <label>Teléfono de Contacto:</label>
                    <input type="text" placeholder="261456216" />

                    <label>Email de Contacto:</label>
                    <input type="email" placeholder="contacto@cliente.com" />

                    <label>Dirección:</label>
                    <input type="text" placeholder="Calle Falsa 443" />

                    <label>Provincia:</label>
                    <input type="text" placeholder="Buenos Aires" />

                    <label>Fecha de Carga:</label>
                    <input type="datetime-local" />

                    <label>Fecha de Entrega:</label>
                    <input type="datetime-local" />

                    <label>Subtotal:</label>
                    <input type="number" placeholder="5000.0" />

                    <label>Total:</label>
                    <input type="number" placeholder="5500.0" />

                    <label>Estado:</label>
                    <input type="text" placeholder="parcial_pendiente" />

                    <label>Pagado:</label>
                    <input type="number" placeholder="1000.0" />

                    <button type="submit">Guardar</button>
                    <button type="button" onClick={toggleModal}>Cancelar</button>
                </form>
            </ModalContent>
        </Modal>
    )
};
export default ModalOrdenes;
