import React from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
//import LogoutButton from './LogoutButton';

const NavbarContainer = styled.nav`
  display: flex;
  justify-content: space-around; /* Espaciado entre los elementos */
  align-items: center;
  background-color: ${({ theme }) => theme.navbarBackground};
  padding: 10px;
  width: 100%;
  box-shadow: 0px 2px 4px rgba(0, 0, 0, 0.1);
`;

const NavbarItem = styled(Link)`
  color: #fff;
  text-decoration: none;
  padding: 10px 20px;
  font-size: 18px;

  &:hover {
    color: ${({ theme }) => theme.primaryButton}; /* Color amarillo al pasar el mouse */
  }
`;

const Button = styled.button`
  background-color: ${({ theme }) => theme.primaryButton};
  color: #000; /* Color del texto */
  border: none;
  padding: 10px 15px;
  font-size: 16px;
  cursor: pointer;
  margin-left: 20px; /* Espaciado entre el bot贸n y la navbar */

  &:hover {
    background-color: #e0a800;
  }
`;

const Navbar = () => {
  return (
    <NavbarContainer>
      <NavbarItem to="/productos">Productos</NavbarItem>
      <NavbarItem to="/ordenes">Ordenes</NavbarItem>
      <NavbarItem>Generacion de reportes</NavbarItem>
      <Button onClick={() => alert('Nueva Orden')}>Nueva Orden</Button> {/* Bot贸n para agregar nueva orden */}
      <Button onClick={() => alert('Agregar Producto')}>Agregar Producto</Button> {/* Bot贸n para agregar producto */}
      {//<LogoutButton />
}
    </NavbarContainer>
  );
};

export default Navbar;
