import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import { ThemeProvider } from 'styled-components';
import Navbar from './components/Navbar';
import OrdersTable from './components/OrdersTable';
import Products from './components/Products';
import { darkTheme } from './components/theme';
import GlobalStyle from './globalStyles';
import styled from 'styled-components';
import { useAuth0 } from '@auth0/auth0-react';
import Modal from 'react-modal';
import { obtenerDatos } from './helper/traeDatos';
import { mostrarOrdenes, mostrarProductos, obtenerIdUsuario } from './helper/url';
import PaginationFooter from './components/footer';
import OrderDetail from './components/OrdenDetalle';

//difinir modal raiz
Modal.setAppElement('#root');

const HeaderContainer = styled.header`
  background-color: ${({ theme }) => theme.background};
  position: fixed;
  top: 0;
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.1);
  z-index: 1000;
  padding: 20px 0;
`;

const Logo = styled.img`
  height: 120px;
  margin-bottom: 10px;
`;

const Title = styled.h1`
  font-size: 36px;
  color: ${({ theme }) => theme.primaryButton}; /* Texto amarillo */
  font-family: 'Montserrat', sans-serif;
  margin-bottom: 10px;
`;

const AppBody = styled.div`
  margin-top: 275px; /* Para que el contenido no se superponga con el encabezado y navbar */
  padding: 20px;
  background: linear-gradient(180deg, #2c2c2c, #000000); /* Degradado gris oscuro a negro */
  min-height: 100vh;
`;

const datosProductos = await obtenerDatos(mostrarProductos(), "GET");

let productosArray;

productosArray = (Array.isArray(datosProductos)) ? datosProductos : Object.values(datosProductos);

const App = () => {
  //console.log(ordenesArray);

  const { user, isAuthenticated, isLoading, loginWithRedirect, getAccessTokenSilently } = useAuth0();
  const [userId, setUserId] = useState(null);

  const [page, setPage] = useState(0);
  const [orders, setOrdenes] = useState([]);
  const [currentPage, setCurrentPage] = useState(0);
  const [idOrden, setIdOrden] = useState(null);


  useEffect(() => {
    if (!isAuthenticated && !isLoading) {
      loginWithRedirect();
      return;
    }
  }, [isAuthenticated, isLoading, loginWithRedirect]);
  useEffect(() => {
    try {
      if (user != null) {
        async function cargar() {
          const token = await getAccessTokenSilently();
          if (!token) {
            return;
          }
          console.log(token);
          const cargarUsuario = await obtenerDatos(obtenerIdUsuario(user.name), "GET", token);

          let id;
          id = await cargarUsuario;
          setUserId(() => id);
        }
        cargar();
      }
      return;
    } catch (e) {
      console.error(e);
      return;
    }
  }, [!userId, isAuthenticated]);
  useEffect(() => {
    async function cargar() {
      const token = await getAccessTokenSilently();
      if (!token) {
        return;
      }
      console.log(token);
      const datos = await obtenerDatos(mostrarOrdenes(currentPage, 10), "GET", token);

      let ordenesArray;

      ordenesArray = (Array.isArray(datos)) ? datos['content'] : Object.values(datos.content);
      setPage(() => datos.totalPages);
      //console.log(page);
      setOrdenes(() => ordenesArray);
    }
    cargar();
  }, [currentPage])

  function onPageChange(pagina) {
    setCurrentPage(pagina);
  }
  function addIdOrden(id) {
    setIdOrden(() => id);
  }

  if (isLoading) {
    return <div>Cargando...</div>;
  }

  return isAuthenticated && (
    <ThemeProvider theme={darkTheme}>
      <GlobalStyle />
      <Router>
        <HeaderContainer>
          <Logo src="/442418039_1441156863179431_493331331508367596_n.jpg" alt="Logo de Chepps" />
          <Title>Chepps</Title>
          {userId && (
            <Navbar userId={userId} />
          )}
        </HeaderContainer>
        <AppBody>
          <Routes>
            <Route path="/" element={<Navigate to="/ordenes" />} />
            <Route path="/ordenes" element={<OrdersTable orders={orders} addIdOrden={addIdOrden} />} />
            <Route path="/productos" element={<Products Productos={productosArray} />} />
            <Route path="/ordenes/:id" element={<OrderDetail />} />
          </Routes>
          <PaginationFooter
            totalPages={page}
            onPageChange={onPageChange}
          />

        </AppBody>
      </Router>
    </ThemeProvider>


  );
};

export default App;
