import React, { useState, useEffect } from "react";
import { ModalContent, Form, Label, Input, Button, Select } from './ModalStyles';
import Modal from 'react-modal';

const ModalProductos = ({ isModalOpen, toggleModal, openConfirmationModal }) => {
    const [selected, setSelectedOption] = useState('No')
    const handleSelectChange = (e) => {
        const option = e.target.value;
        setSelectedOption(option);
    };
    useEffect(() => {
        console.log(selected);
    }, [selected])

    return (
        <Modal isOpen={isModalOpen} onRequestClose={toggleModal} contentLabel="Formulario de Nueva Orden" style={{
            overlay: {
                background: "linear-gradient(135deg, #0a192f,rgb(62, 71, 83))", // ðŸ”¹ Azul oscuro con degradado
            },
            content: {
                width: "50%",
                margin: "auto",
                padding: "20px",
                borderRadius: "8px",
                background: "black",
                border: "none"
            },
        }}>
            <ModalContent>
                <h1>Nuevo producto</h1>
                <hr />
                <Form>

                    <Label>Denominacion:</Label>
                    <Input required />
                    <h3>Detalles del producto:</h3>
                    <Label>Â¿Es insumo?</Label>
                    <Select onChange={handleSelectChange}>
                        <option value={'No'}>No</option>
                        <option value={'Si'}>Si</option>
                    </Select>

                    <Button>Aceptar</Button>
                    <Button>Cancelar</Button>
                </Form>
            </ModalContent>
        </Modal>
    )
}

export default ModalProductos
