import styled from 'styled-components';

const RadioGroup = styled.div`
  display: flex;
  flex-direction: column;
  gap: 1rem;
  padding: 1rem;
  background-color: #1e1e1e;
  border-radius: 8px;
  color: #f0f0f0;
  max-width: 100%;
`;

const RadioOption = styled.div`
  display: flex;
  align-items: center;
  gap: 0.5rem;
`;

const RadioInput = styled.input`
  accent-color: #4f46e5;
  width: 18px;
  height: 18px;
`;

const RadioLabel = styled.label`
  font-size: 1rem;
  cursor: pointer;

  @media (max-width: 600px) {
    font-size: 0.95rem;
  }
`;

const MarcaRadioGroup = ({ optionMarca, setOptionMarca }) => {
  return (
    <RadioGroup>
      <RadioOption>
        <RadioInput
          type="radio"
          id="ninguna"
          name="marca"
          value="Ninguna"
          checked={optionMarca === 'Ninguna'}
          onChange={(e) => setOptionMarca(e.target.value)}
        />
        <RadioLabel htmlFor="ninguna">Sin marca</RadioLabel>
      </RadioOption>

      <RadioOption>
        <RadioInput
          type="radio"
          id="nueva"
          name="marca"
          value="Nueva"
          checked={optionMarca === 'Nueva'}
          onChange={(e) => setOptionMarca(e.target.value)}
        />
        <RadioLabel htmlFor="nueva">Nueva marca</RadioLabel>
      </RadioOption>

      <RadioOption>
        <RadioInput
          type="radio"
          id="elegir"
          name="marca"
          value="Elegir"
          checked={optionMarca === 'Elegir'}
          onChange={(e) => setOptionMarca(e.target.value)}
        />
        <RadioLabel htmlFor="elegir">Elegir una marca</RadioLabel>
      </RadioOption>
    </RadioGroup>
  );
};

export default MarcaRadioGroup;
