import { useEffect, useState } from 'react';
import { obtenerDatos } from '../helper/traeDatos';
import { insumoFindById } from '../helper/url';
import { useParams } from 'react-router-dom';
import styled from 'styled-components';

const Container = styled.div`
  max-width: 800px;
  margin: auto;
  padding: 20px;
  background-color: #222;
  color: #fff;
  border-radius: 10px;

  @media (max-width: 600px) {
    padding: 15px;
  }
`;

const Title = styled.h1`
  font-size: 1.8rem;
  margin-bottom: 20px;
`;

const FieldGroup = styled.div`
  display: flex;
  flex-direction: column;
  margin-bottom: 15px;

  label {
    margin-bottom: 5px;
    font-weight: bold;
  }

  input {
    padding: 10px;
    border-radius: 6px;
    border: none;
    background-color: #333;
    color: #fff;
    font-size: 1rem;

    &:focus {
      outline: 2px solid #555;
    }
  }
`;

const ProductoDetalleInfo = ({ token, manufacturado = true }) => {
    const { id } = useParams();
    const [producto, setProducto] = useState(null);

    useEffect(() => {
        async function cargar() {
            if (id != null && token) {
                const url = insumoFindById(id);
                const data = await obtenerDatos(url, 'GET', token);
                setProducto(data);
            }
        }
        cargar();
    }, [id, token]);

    const handleChange = (field, value) => {
        setProducto(prev => ({
            ...prev,
            [field]: value
        }));
    };

    if (!producto) {
        return <h2>Cargando...</h2>;
    }

    return (
        <Container>
            <Title>Detalle de producto #{producto.idInsumo}</Title>

            <FieldGroup>
                <label>Denominación</label>
                <input
                    type="text"
                    value={producto.denominacion || ''}
                    onChange={(e) => handleChange('denominacion', e.target.value)}
                />
            </FieldGroup>

            <FieldGroup>
                <label>Precio</label>
                <input
                    type="number"
                    value={producto.precio || ''}
                    onChange={(e) => handleChange('precio', e.target.value)}
                />
            </FieldGroup>

            <FieldGroup>
                <label>Es para elaborar</label>
                <input
                    type="checkbox"
                    checked={producto.esParaElaborar || false}
                    onChange={(e) => handleChange('esParaElaborar', e.target.checked)}
                />
            </FieldGroup>

            <FieldGroup>
                <label>Marca</label>
                <input
                    type="text"
                    value={producto.marca?.nombre || ''}
                    onChange={(e) =>
                        setProducto(prev => ({
                            ...prev,
                            marca: { ...prev.marca, nombre: e.target.value }
                        }))
                    }
                />
            </FieldGroup>

            <FieldGroup>
                <label>Detalle (stock actual / mínimo)</label>
                <input
                    type="text"
                    value={
                        producto.detalle
                            ? `${producto.detalle.stockActual} / ${producto.detalle.stockMinimo}`
                            : ''
                    }
                    readOnly
                />
            </FieldGroup>
        </Container>
    );
};

export default ProductoDetalleInfo;
