import React from 'react';
import styled from 'styled-components';

const TableContainer = styled.div`
  margin: 20px 0;
`;

const Table = styled.table`
  width: 100%;
  border-collapse: collapse;
  background-color: #333;
  color: #fff;
`;

const TableHeader = styled.th`
  padding: 10px;
  border-bottom: 2px solid #444;
`;

const TableData = styled.td`
  padding: 10px;
  border-bottom: 1px solid #444;
`;
const ButtonViewDetail = styled.button`
  padding: 10px 15px; /* Ajuste de padding */
  background-color: #000; /* Fondo negro */
  color: #ff0000; /* Letras rojas */
  border: 2px solid #ff0000; /* Borde rojo */
  border-radius: 5px; /* Bordes redondeados */
  font-size: 16px; /* Tamaño de letra */
  font-weight: bold; /* Negrita */
  cursor: pointer; /* Indicador de clic */
  transition: all 0.3s ease; /* Transición para efectos */

  &:hover {
    background-color: #222; /* Fondo más claro al pasar el cursor */
    color: #fff; /* Letras blancas */
    border-color: #fff; /* Borde blanco */
  }

  &:active {
    transform: scale(0.95); /* Pequeño efecto de clic */
  }
`;

const ButtonRemove = styled.button`
  padding: 10px 15px; /* Ajuste de padding */
  background-color: #000; /* Fondo negro */
  color: #ff0000; /* Letras rojas */
  border: none; /* Sin borde */
  border-radius: 5px; /* Bordes redondeados */
  font-size: 16px; /* Tamaño de letra */
  font-weight: bold; /* Negrita */
  cursor: pointer; /* Indicador de clic */
  transition: all 0.3s ease; /* Transición para efectos */

  &:hover {
    background-color: #222; /* Fondo más claro al pasar el cursor */
    color: #ff4d4d; /* Letras más claras al pasar el cursor */
  }

  &:active {
    transform: scale(0.95); /* Pequeño efecto de clic */
  }
`;

const OrdersTable = ({ orders, handleDelete }) => {
  if (!orders || orders.length === 0) {
    return <h1>Tabla vacia.</h1>
  }
  return (
    <TableContainer>
      <Table>
        <thead>
          <tr>
            <TableHeader>ID</TableHeader>
            <TableHeader>Cliente</TableHeader>
            <TableHeader>Total</TableHeader>
            <TableHeader>Pagado</TableHeader>
            <TableHeader>Orden</TableHeader>
          </tr>
        </thead>
        <tbody>
          {orders.map((order) => (
            <tr key={order.idOrden}>
              <TableData>#{order.idOrden}</TableData>
              <TableData> {order.cliente}</TableData>
              <TableData> {order.total}</TableData>
              <TableData> <strong>$</strong>{order.pagado}</TableData>
              <TableData>
                <ButtonViewDetail>Ver detalle</ButtonViewDetail>
                <ButtonRemove onClick={() => handleDelete(order.id)}>Eliminar</ButtonRemove>
              </TableData>
            </tr>
          ))}
        </tbody>
      </Table>
    </TableContainer>
  );
};

export default OrdersTable;
