import React, { useState, useEffect } from 'react';
import Modal from 'react-modal';
import styled from 'styled-components';
import { obtenerDatos } from '../helper/traeDatos';
import { ProductoDetalle } from './ProductoDetalle';

const Select = styled.select`
  width: 87%;
  padding: 10px;
  margin-bottom: 10px;
  margin-right: 10px;
  margin-left: 10px;
  background-color: #555; /* Fondo gris para los campos */
  color: #fff;
  border: none;
  border-radius: 4px;
  font-size: 16px;
  outline: none;

  &::placeholder {
    color: #ccc;
  }
`;
const ModalContent = styled.div`
  background: #333;
  padding: 20px;
  border-radius: 8px;
  color: #fff;
  max-width: 500px;
  width: 100%;
  height: auto;
  margin: 0 auto;
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
  gap: 15px;
  align-items: center;
`;

const Label = styled.label`
  width: 100%;
  color: #bbb;
  font-size: 14px;
`;

const Input = styled.input`
  width: 100%;
  padding: 10px;
  background-color: #555;
  color: #fff;
  border: none;
  border-radius: 4px;
  font-size: 16px;
  outline: none;

  &::placeholder {
    color: #ccc;
  }
`;

const Button = styled.button`
  background-color: #e0a800;
  color: #000;
  border: none;
  padding: 10px 20px;
  font-size: 16px;
  border-radius: 4px;
  cursor: pointer;
  margin-top: 10px;

  &:hover {
    background-color: #ffd700;
  }
`;

const ModalOrdenes = ({ isModalOpen, toggleModal }) => {
  const [sumaTotal, setSumaTotal] = useState(0);
  const [productos, setProductos] = useState([]);
  const [insumos, setInsumos] = useState([]);
  const [clientes, setClientes] = useState([]);
  const [detalles, setDetalles] = useState([]);
  const [selectedItem, setSelectedItem] = useState(null);

  const handleSelectChange = (e) => {
    const selectedItemid = parseInt(e.target.value, 10);
    const item = clientes.find((item) => item.idCliente === selectedItemid);
    setSelectedItem(item);

  };


  useEffect(() => {
    const cargarProductosYClientes = async () => {
      try {
        const url = "http://localhost:8080/public/productos";
        const urlClientes = "http://localhost:8080/public/clientes";

        const urlInsumos = "http://localhost:8080/public/insumo/retornarTodos";

        const dataProductos = await obtenerDatos(url, 'GET');
        const dataClientes = await obtenerDatos(urlClientes, 'GET');
        const dataInsumo = await obtenerDatos(urlInsumos, 'GET');
        setProductos(dataProductos);
        setClientes(dataClientes);
        setInsumos(dataInsumo);
      } catch (error) {
        console.error('Error al cargar los productos:', error);
      }
    };

    cargarProductosYClientes();
  }, []);

  // Función para agregar un nuevo ProductoDetalle
  const handleAgregarDetalle = () => {
    if (productos.length > 0) {
      setDetalles([...detalles, { productos, insumos }]);
    } else {
      console.warn('Productos no disponibles para agregar.');
    }
  };

  // Función para quitar el último ProductoDetalle
  const handleQuitarDetalle = () => {
    if (detalles.length > 0) {
      // Eliminar el último detalle y restar su suma del total
      const ultimoDetalle = detalles[detalles.length - 1];
      setSumaTotal((prevSumaTotal) => prevSumaTotal - ultimoDetalle.suma);
      setDetalles((prevDetalles) => prevDetalles.slice(0, -1));
    }
  };

  const actualizarSumaDetalle = (id, nuevaSuma) => {
    setDetalles((prevDetalles) => {
      // Actualizamos el detalle específico
      const nuevosDetalles = prevDetalles.map((detalle) =>
        detalle.id === id ? { ...detalle, suma: nuevaSuma } : detalle
      );

      // Recalculamos la suma total usando los nuevos detalles
      const nuevaSumaTotal = nuevosDetalles.reduce(
        (total, detalle) => total + detalle.suma,
        0
      );

      // Actualizamos la suma total
      setSumaTotal(nuevaSumaTotal);

      return nuevosDetalles; // Retornamos el nuevo estado de los detalles
    });
  };

  const enviarDatos = () => {
    return null;
  };

  return (
    <Modal isOpen={isModalOpen} onRequestClose={toggleModal} contentLabel="Formulario de Nueva Orden">
      <ModalContent>
        <h2 style={{ textAlign: 'center' }}>Nueva Orden</h2>
        <Form>
          <Label>Cliente</Label>
          <Select onClick={handleSelectChange}>
            <option>Seleccione uno...</option>
            {clientes.map((cliente) => (
              <option key={cliente.idCliente} value={cliente.idCliente}>{cliente.nombre}  {(cliente.apellido) = ! null ? cliente.apellido : ''}</option>
            ))}
          </Select>
          <Label>Domicilio</Label>
          <Input type='tex' />
          <Button type="button" onClick={handleAgregarDetalle}>
            Agregar línea de producto
          </Button>
          <Button type="button" onClick={handleQuitarDetalle} disabled={detalles.length === 0}>
            Quitar Último Detalle del Producto
          </Button>

          {
            detalles.map((detalle, index) => (
              <ProductoDetalle key={index} productos={detalle.productos} insumos={detalle.insumos} onActualizarSumaTotal={(nuevaSuma) => actualizarSumaDetalle(detalle.id, nuevaSuma)} />
            ))
          }

          < Label > Fecha de Carga:</Label>
          <Input type="datetime-local" />

          <Label>Fecha de Entrega:</Label>
          <Input type="datetime-local" />
          <Label>Tipo: </Label>
          <Select>
            <option>PAGO</option>
            <option>COMPRA</option>
            <option>VENTA</option>
          </Select>

          <Label>Pagado:</Label>
          <Input type="number" placeholder="" />

          <h3>Total calculado:<span>${sumaTotal}</span></h3>

          <Button type="submit" onClick={enviarDatos}>Guardar</Button>
          <Button type="button" onClick={toggleModal}>Cancelar</Button>
        </Form>
      </ModalContent>
    </Modal>
  );
};

export default ModalOrdenes;
