import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import LogoutButton from './LogoutButton';
import ModalOrdenes from './modalOrdenes';
import ModalProductos from './ModalProductos';
import ConfirmationModal from './ModalConfirm';

const NavbarContainer = styled.nav`
  display: flex;
  justify-content: space-between;
  align-items: center;
  background-color: ${({ theme }) => theme.navbarBackground};
  padding: 15px 20px;
  width: 100%;
  box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.1);
  position: fixed;
  top: 0;
  left: 0;
  z-index: 1000;

  @media (max-width: 768px) {
    flex-direction: column;
    gap: 10px;
    padding: 10px;
  }
`;

const NavLinks = styled.div`
  display: flex;
  gap: 20px;

  @media (max-width: 768px) {
    flex-direction: column;
    align-items: center;
  }
`;

const NavbarItem = styled(Link)`
  color: #fff;
  text-decoration: none;
  font-size: 18px;
  font-weight: 500;
  padding: 8px 12px;
  transition: color 0.3s ease-in-out;

  &:hover {
    color: ${({ theme }) => theme.primaryButton};
  }
`;

const Button = styled.button`
  background-color: ${({ theme }) => theme.primaryButton};
  color: #000;
  border: none;
  padding: 10px 15px;
  font-size: 16px;
  cursor: pointer;
  border-radius: 5px;
  transition: all 0.3s ease-in-out;
  box-shadow: 0px 2px 4px rgba(0, 0, 0, 0.2);

  &:hover {
    background-color: #e0a800;
    transform: scale(1.05);
  }
`;

const Navbar = ({ userId }) => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isModalOpenProductos, setIsModalOpenProductos] = useState(false);
  const [isConfirmationModalOpen, setIsConfirmationModalOpen] = useState(false);

  const toggleModal = () => setIsModalOpen(!isModalOpen);
  const toggleModalProductos = () => setIsModalOpenProductos(!isModalOpenProductos);
  const openConfirmationModal = () => setIsConfirmationModalOpen(true);
  const closeConfirmationModal = () => setIsConfirmationModalOpen(false);

  return (
    <NavbarContainer>
      <NavLinks>
        <NavbarItem to="/productos">Productos</NavbarItem>
        <NavbarItem to="/ordenes">Órdenes</NavbarItem>
        <NavbarItem to="#">Generación de reportes</NavbarItem>
      </NavLinks>
      <NavLinks>
        <Button onClick={toggleModal}>Nueva Orden</Button>
        <Button onClick={toggleModalProductos}>Agregar Producto</Button>
        <LogoutButton />
      </NavLinks>
      <ModalOrdenes isModalOpen={isModalOpen} toggleModal={toggleModal} openConfirmationModal={openConfirmationModal} userId={userId} />
      <ModalProductos isModalOpen={isModalOpenProductos} toggleModal={toggleModalProductos} openConfirmationModal={openConfirmationModal} />
      <ConfirmationModal isOpen={isConfirmationModalOpen} onClose={closeConfirmationModal} />
    </NavbarContainer>
  );
};

export default Navbar;
