const MarcaRadioGroup = ({ optionMarca, setOptionMarca }) => {
    return (
        <div className="radio-group">
            <div className="radio-option">
                <input
                    type="radio"
                    id="ninguna"
                    name="marca"
                    value="Ninguna"
                    checked={optionMarca === 'Ninguna'}
                    onChange={(e) => setOptionMarca(e.target.value)}
                />
                <label htmlFor="ninguna">Sin marca</label>
            </div>

            <div className="radio-option">
                <input
                    type="radio"
                    id="nueva"
                    name="marca"
                    value="Nueva"
                    checked={optionMarca === 'Nueva'}
                    onChange={(e) => setOptionMarca(e.target.value)}
                />
                <label htmlFor="nueva">Nueva marca</label>
            </div>

            <div className="radio-option">
                <input
                    type="radio"
                    id="elegir"
                    name="marca"
                    value="Elegir"
                    checked={optionMarca === 'Elegir'}
                    onChange={(e) => setOptionMarca(e.target.value)}
                />
                <label htmlFor="elegir">Elegir una marca</label>
            </div>

            <style jsx>{`
        .radio-group {
          display: flex;
          flex-direction: column;
          gap: 1rem;
          padding: 1rem;
          background-color: #1e1e1e;
          border-radius: 8px;
          color: #f0f0f0;
          max-width: 100%;
        }

        .radio-option {
          display: flex;
          align-items: center;
          gap: 0.5rem;
        }

        input[type='radio'] {
          accent-color: #4f46e5; /* Indigo for dark themes */
          width: 18px;
          height: 18px;
        }

        label {
          font-size: 1rem;
          cursor: pointer;
        }

        @media (max-width: 600px) {
          .radio-group {
            gap: 0.75rem;
            padding: 0.75rem;
          }

          label {
            font-size: 0.95rem;
          }
        }
      `}</style>
        </div>
    );
};

export default MarcaRadioGroup;
