import { useState, useEffect } from "react";
import Modal from 'react-modal';
import { ModalContent, Form, Input, Label, Button, Select } from './ModalStyles';
import { obtenerDatos } from '../helper/traeDatos';
import { mostrarProductos, getInsumos, excelPorFecha } from '../helper/url';

function ModalReportes({ isModalOpen, toggleModal, token }) {
    const [tipo, setTipo] = useState();
    const [productos, setProductos] = useState([]);
    const [fecha, setFecha] = useState(null);
    const [insumos, setInsumos] = useState();
    const [id, setId] = useState();

    const url = mostrarProductos();
    const urlInsumos = getInsumos();

    const handlerTipo = (e) => {
        setTipo(e.target.value);
    };
    const handlerAceptar = () => {
        if (tipo == 'orden') {
            const enviado = cargarEnviarDatos(true);
            if (enviado) {
                toggleModal();
            }
        }
    };
    const handlerProducto = (e) => {
        setId(e.target.value);
    };

    const cargarEnviarDatos = async (enviar = false) => {
        if (tipo == 'insumo' && !enviar) {
            const dataInsumo = await obtenerDatos(urlInsumos, 'GET', token);
            setInsumos(dataInsumo);
        }
        if (tipo == 'producto' && !enviar) {
            const dataProductos = await obtenerDatos(url, 'GET', token);
            setProductos(dataProductos);
        }
        if (enviar) {
            const urlExcel = excelPorFecha(fecha);
            console.log(urlExcel);
            const data = await obtenerDatos(urlExcel, 'GET', token);
            return true;
        }
    }
    useEffect(() => {
        if (tipo != null) {
            cargarEnviarDatos();
        }
    }, [tipo]);

    return (
        <Modal isOpen={isModalOpen} onRequestClose={toggleModal} contentLabel="Formulario de generacion de reportes" style={{
            overlay: {
                background: "rgba(0, 0, 0, 0.8)",
            },
            content: {
                width: "90%",
                maxWidth: "600px",
                margin: "auto",
                padding: "2rem",
                borderRadius: "16px",
                background: "#121212",
                border: "none",
                boxShadow: "0 10px 30px rgba(0, 0, 0, 0.5)"
            }
        }}>
            <ModalContent>
                <h1>Generar nuevo reporte</h1>

                <Form>
                    <Label htmlFor="tipo">Tipo de reporte:</Label>
                    <Select name="tipo" onChange={handlerTipo}>
                        <option>Seleccionar uno...</option>
                        <option value='orden'>Reporte de ventas</option>
                        <option value='producto'>Reporte de ventas de un producto</option>
                        <option value='insumo'>Reporte de ventas de un insumo</option>
                    </Select>
                    {tipo == 'orden' && (
                        <>
                            <Label htmlFor='fecha'>Fecha:</Label>
                            <Input type="datetime-local" name="fecha" onChange={(e) => setFecha(e.target.value)} />
                        </>
                    )}
                    {tipo == 'producto' && (
                        <>
                            <Label htmlFor="productos">Producto:</Label>
                            <Select name="productos">
                                <option>Seleccionar uno...</option>
                                {productos.map((p) => (
                                    <option key={p.idProductoManufacturado}>{p.denominacion}</option>
                                ))}
                            </Select>
                        </>
                    )}
                    <Button type="button" onClick={handlerAceptar}>Aceptar</Button>
                    <Button type="button" onClick={toggleModal}>Cancelar</Button>
                </Form>
            </ModalContent>
        </Modal>
    )
}
export default ModalReportes;
